/**
 * Adds an info icon to the label with a tooltip message
 * If a parent label is not found then does not do anything
 * @param {string} message
 */
pb.fields.AbstractBase.prototype.addHelpText = function (message) {
    var infoIcon = this.getInfoIcon_();
    if (!infoIcon) return;
    var existingMessage = infoIcon.getAttribute("title");
    if (existingMessage != null && existingMessage != "") {
        message = existingMessage + "\n" + message;
    }
    infoIcon.setAttribute("title", message);
};

/**
 * Get the info icon, used by both addHelpText and setHelpText. This creates 
 * an info icon if one does not exist.
 * @returns {Element|null} null if it was unable to create the help icon.
 */
pb.fields.AbstractBase.prototype.getInfoIcon_ = function () {
    var container = this.getContainerElement();
    if (!container) return;

    if (!(container.tagName && container.tagName.toUpperCase() == "LI")){
        container = goog.dom.findNode(container, function (node) {
            if (node.tagName && node.tagName.toUpperCase() == "LI") return true;
        });
    }
    if (!container) return;

    var label = this.getLabel();
    if (!label) return;

    // Check if an info icon already exists
    var infoIcon = goog.dom.query(".infoIcon", label)[0];
    if (!infoIcon) { 
        infoIcon = this.dom_.createDom("span", { "class": "infoIcon" });

        // Have to put the existing content inside a span so that the icon
        // won't be pushed to a new line
        label.innerHTML = "<span class=\"infoIconLabel\">" + label.innerHTML + "</span>";
        goog.dom.classes.add(label, "infoIconContainer");
        label.appendChild(infoIcon);
    }

    return infoIcon;
};

/**
 * Get the container element
 * @returns {Element|null} Returns the container element (li element) or null if 
 * there isn't one.
 */
pb.fields.AbstractBase.prototype.getContainerElement = function () {
    return goog.dom.getAncestor(this.element_, function (node) {
        if (node["tagName"] && node["tagName"].toLowerCase() == "li" 
        // Reverting to previous version for Wednesday's release.
        //if (node["tagName"] && node["tagName"].toLowerCase() == "div" 
            && goog.dom.classes.has(node, "co-cell"))
            return true;
    });
};

/**
 * Get the label element, this navigates the DOM looking for it, so if the
 * element has been placed in an unusual place this might return null.
 * @returns {Element|null} Returns the label element or null if 
 * there isn't one that's paired with the field.
 */
pb.fields.AbstractBase.prototype.getLabel = function () {
    var label = goog.dom.getElement("ctl00_cntMainBody_lbl" + this.getObjectName() + "_" + this.getPropertyName());
    if (label) return label;

    // Try more long winded way.

    var container = this.getContainerElement();
    if (!container) return null;

    return goog.dom.findNode(container, function (node) {
        return node.tagName && node.tagName.toLowerCase() == "label";
    });
};

