pb.fields.AbstractBase.prototype.markCaller = function (caller) {

    if (this.level == null)
        this.level = 0;
    else
        this.level++;

    pb.Field.Callers = pb.Field.Callers || {};
    pb.Field.Callers[this.level] = pb.Field.Callers[this.level] || {};
    pb.Field.Callers[this.level][this.strObject] = pb.Field.Callers[this.level][this.strObject] || {};
    pb.Field.Callers[this.level][this.strObject][this.strProperty] = pb.Field.Callers[this.level][this.strObject][this.strProperty] || [];

    pb.Field.Callers[this.level][this.strObject][this.strProperty].push(caller);
};

pb.fields.AbstractBase.prototype.isCallerMarked = function (caller) {
    if (!(
        pb.Field.Callers
        && pb.Field.Callers[this.level]
        && pb.Field.Callers[this.level][this.strObject]
        && pb.Field.Callers[this.level][this.strObject][this.strProperty]
        ))
        return false;

    // Check the last caller added to each level, as this represents the current branch
    var lastOfEachLevel = false;
    for (var key in pb.Field.Callers) {
        if (!pb.Field.Callers.hasOwnProperty(key))
            continue;
        lastOfEachLevel = goog.array.some(pb.Field.Callers[key][this.strObject][this.strProperty], function (localCaller) {
            if (localCaller == caller)
                return true;
        }, this);
        if (lastOfEachLevel == true)
            break;
    }
    var lastLevel = goog.array.some(pb.Field.Callers[this.level][this.strObject][this.strProperty], function (localCaller) {
        if (localCaller == caller)
            return true;
    }, this);

    return (lastOfEachLevel || lastLevel);
};

pb.fields.AbstractBase.prototype.clearCallers = function () {
    pb.Field.Callers = null;
    this.level = null;
    this.level--;
};

