/**
 * Get attribute
 */
pb.xml.Node.prototype.getAttribute = function (attr) {
    attr = attr.toUpperCase();
    return this.node_.getAttribute(attr);
};


/**
 * Select nodes using an Xpath expression
 * @param {pb.xml.Document} node
 * @param {string} path XPath expression
 */
pb.xml.Node.prototype.selectNodes = function (path) {

    path = path.toUpperCase();

    var nodes;
    if ((typeof xpath) !== 'undefined'){
        var nodes = xpath.select(path, this.node_);
    } else if (goog.userAgent.IE || !!window.MSStream) {
        nodes = this.node_.selectNodes(path);
    } else {
        nodes = goog.dom.xml.selectNodes(this.node_, path);
    }
    return goog.array.map(nodes, function (node) {
        return new pb.xml.Node(node);
    }, this);
};



