/**
 * @param {goog.dom.DomHelper=} opt_domHelper Optional DOM helper.
 * @constructor
 * @extends {goog.ui.Component}
 */
pb.fields.XmlDataSet = function (strObject, strProperty, opt_domHelper) {
    goog.base(this, strObject, strProperty, opt_domHelper);
    
    this["valueOf"] = this.getValue;

    this.xmlDataSet_ = pb.xml.Document.loadXml(window["XMLDataSet"]);
    this.currentNode_ = this.xmlDataSet_.getCurrentNode();
    if (this.currentNode_.node_
        // Fails in IE
        //&& this.currentNode_.node_ instanceof Element
        && this.currentNode_.node_.tagName 
        && this.currentNode_.node_.tagName.toUpperCase() == strObject.toUpperCase()) {
        this.objectNode_ = this.currentNode_;
    } else {
        // Try and traverse up to find a match
        var node = this.currentNode_.node_;
        while (node.tagName.toUpperCase().indexOf("_POLICY_BINDER") == -1){
          if (node.tagName.toUpperCase() == strObject.toUpperCase()){
            this.objectNode_ = node;
            break;
          }
          node = node.parentNode;
        };
		
        if (this.objectNode_ == null){
          this.objectNode_ = this.currentNode_.selectNodes(".//" + strObject)[0]
            || this.currentNode_.selectNodes("//" + strObject)[0];
        }
    }
    if (this.objectNode_ != null) {
        // If it's not a property it may be a child object. TODO
        this.value_ = this.objectNode_.getAttribute(strProperty.toUpperCase());
    } else {
        this.value_ = null;
        if (this.value_ == null)
        {
            throw "Field with object '" + strObject + "' and property '" + strProperty + "' does not exist in the XML Data Set.";
        }
    }
};
