/**
 * Set the value of the element. 
 * Use <span class="highlight">true</span> and <span class="highlight">false</span> for checkboxes. 
 * @param {Mixed} value
 */
pb.fields.General.prototype.setValue = function (value) {

    // --- Prevent infinite loop code ---
    if (this.constructor == pb.fields.General && this.busy) return;
    // Do not want to call this if triggered from a sub class
    if (this.constructor == pb.fields.General) this.busy = true;
    // --- Prevent infinite loop code END ---

    // Do not allow setting a value if in 'View' mode.
    if (this.unchangeable) { this.busy = false; return; }

    if (value != this.getValue()){
      this.setValueInternal(value);
      this.dispatchEvent("change");
    }

    // --- Prevent infinite loop code ---
    // Do not want to call this if triggered from a sub class
    if (this.constructor == pb.fields.General) this.busy = false;
    // --- Prevent infinite loop code END ---

};

/**
 * Set the value without triggering a change event
 */
pb.fields.General.prototype.setValueInternal = function (value) {

    // Do not allow setting a value if in 'View' mode.
    if (this.unchangeable) { this.busy = false; return; }

    if (value instanceof Promise){
        var self = this;
        value.then(function(value){
            self.setValueInternal(value);
        });
        return;
    }

    if (value instanceof pb.fields.AbstractBase) {
        value = value.getValue();
    }

    value = (value != null) ? value.toString() : "";
    if (this.element_.tagName.toLowerCase() == "input") {
        this.element_.value = (value == null) ? "" : value;
    } else if (this.element_.tagName.toLowerCase() == "select") {
        this.element_.value = (value == null) ? "" : value;
    } else {
        this.element_.innerHTML = (value == null) ? "" : value;
    }
    if (this.getHiddenInput() != null) {
        this.getHiddenInput().value = (value == null) ? "" : value;
    }

};


