/**
 * Ideally use this to get an instance, this will prevent clones of the same field being made.
 * As much compatability has been added in as possible so the 'new Field()' format can be used. But 
 * occasionally this creates undesired affects
 */
pb.Field.getInstance = function (strObject, strProperty) {


    
    if (strObject.indexOf(".") != -1) {
        // Try and grab both the object and property from strObject.
        // As if it's in the format obj.property.
        var temp = strObject.split(".");
        if (temp.length < 2 || temp.length > 2) {
            var error = new Error("Invalid Object Property '" + strObject + "'");
            error.display();
            throw error;
        }
        strObject = temp[0].toUpperCase();
        strProperty = temp[1];
    } else {
        strObject = strObject.toUpperCase();
        strProperty = strProperty;
    }


    // All field types should move into being handled by the new component 
    // method, but while the code change over takes time, check if it is handled
    // there first before using the old method
    if (pb.Field.standAloneInstances && pb.Field.standAloneInstances[strObject]
        && pb.Field.standAloneInstances[strObject][strProperty]) {
        return pb.Field.standAloneInstances[strObject][strProperty];
    }


    var field;
    if (pb.Field.instances
        && pb.Field.instances[strObject]
        && pb.Field.instances[strObject][strProperty]) {
        field = pb.Field.instances[strObject][strProperty][0] || null;    
    }
    
    if (field == null) {
        // Assume it's a field that isn't on the page
        try {
            field = new pb.fields.XmlDataSet(strObject, strProperty);
            pb.Field.registerInstance(field);
        } catch (e) {

            // Try the general field lookup
            // Doing this after xml data set, so that an array of child objects
            // from the xml data set can be returned rather than the child screen
            // div container.
            if (field == null) {
                // It may be on the page but not handled with a javascript field object
                // this may have been done by mistake, so check to see if it exists
                // and treat it as a text field.
                var input = goog.dom.getElement("ctl00_cntMainBody_" + strObject
                    + "__" + strProperty);
                if (input) {
                    field = new pb.fields.General(strObject, strProperty);
                    field.decorate(input);
                    Field.registerInstance(field);
                    return field;
                }
            }      

            //throw e;
            // It may be the case that a field exists in reality but hasn't been
            // added to the XML Data Set because it hasn't had a value set.
            // Ideally all fields should be in the Xml Data Set but this is 
            // something outside of our control.
            // For this reason a null field type has been added and a warning 
            // that it can't find the field will be logged.
            // It raises the issue though now that it can't tell the difference
            // between a real field with no value set or a field that doesn't 
            // exist. 
            field = new pb.fields.Null(strObject, strProperty);
            //if (goog.DEBUG) {
                var msg = "Field with object '" + strObject + "' and property '" + strProperty + "' was not found in the XML data set. "
                    + "Either it is spelled incorrectly or has a value of null on another page.";
                goog.debug.Logger.getLogger('pb.Field').warning(msg);
                // If compiled, logger won't work
                if (window["console"] && window["console"]["log"]) {
                    window["console"]["log"](msg);
                }
            //}
            //throw "Field with object '" + strObject + "' and property '" + strProperty + "' does not exist.";
        }
    }
    return field;
};


/**
 * Parse a control id string to get the object and property from it.
 * @returns {Array} An array with two parts, the first the object, the second the property.
 */
pb.Field.getObjectAndPropertyFromId = function (idString) {
    idString = idString.slice(18);
    var parts = idString.split("_");
    var prop = parts.pop();

    var a = parts.length - 1;
    while (a > 0 && parts[a] == "") {
        a--;
        parts.pop();
    }

    var objec = parts.join("_");

    return [objec, prop];
};



/**
 * Get a Field instance from element
 */
pb.Field.getInstanceFromElement = function (element) {
    var parts = pb.Field.getObjectAndPropertyFromId(element.id);
    if (parts.length != 2) {
        throw "Invalid element";
    }


    return pb.Field.getInstance(parts[0], parts[1]);
};



