goog.provide('pb.XMLDataSetReader');
goog.provide('pb.FieldObjectNode');
goog.require('goog.dom.xml');
goog.require('goog.array');
goog.require('goog.userAgent');


/**
 * @constructor
 * @param {XMLNode} node
 */
pb.FieldObjectNode = function (node) {
    this.node = node;
    this["node"] = this.node;

    this["getObjects"] = this.getObjects;
    this["getPropertyValue"] = this.getPropertyValue;
    this["getObject"] = this.getObject;
    this["findDeepestUCNode"] = this.findDeepestUCNode;
}


/** 
 * @constructor
 * This is the same as FieldObjectNode except it is the root node
 */
pb.XMLDataSetReader = function (xml) {

    var root
    if (goog.userAgent.IE || !!window.MSStream) {
        // Have to remove part of the xml or it won't load
        xml = xml.replace(/<!DOCTYPE\sDATA_SET\s*\[.*\]>/ig, "");
        xml = xml.replace(/<\?xml(.*)?\?>/ig, "");
        var xmlReader = goog.dom.xml.loadXml(xml);
        root = xmlReader.firstChild;
    } else {
        var xmlReader = goog.dom.xml.loadXml(xml);
        root = goog.dom.xml.selectSingleNode(xmlReader, "DATA_SET");
    }
    
    if (root == null) {
        var error = new Error("Invalid Page XML Data");
        error.display();
        throw error;
    }
    var dataModelCode = root.getAttribute("DataModelCode");
    var riskObject = this.selectChildNode(root, "RISK_OBJECTS");
    

    var policy = this.selectChildNode(riskObject, dataModelCode + "_POLICY_BINDER");
    goog.base(this, policy);

};
goog.inherits(pb.XMLDataSetReader, pb.FieldObjectNode);


/**
 * Find the object node
 */
pb.XMLDataSetReader.prototype.getObject = function (strObject) {
    // Find deepest UC node
    if (this.node.nodeName == strObject) {
        return this;
    }

    var uc = this.findDeepestUCNode();
    if (uc == null) {
        // Try and find on the first level
        return this.getObjects(strObject)[0] || null;
    }
    return uc.getObject(strObject);
};

/**
 * Find the object node
 */
pb.FieldObjectNode.prototype.getObject = function (strObject) {
    // Find deepest UC node
    if (this.node.nodeName == strObject){
        return this;
    } else {
        var parent = this.getParentNode();
        if (parent == null)
            return null;
        var temp = goog.array.find(parent.getObjects(), function (obj) {
            // Assumes node names are unique
            if (obj.node.nodeName == this.node.nodeName)
                return null;
            return obj.getObject(strObject);
        }, this);
        if (temp == null) {
            return parent.getObject(strObject);
        } else {
            return temp;
        }
        
    }
    
};

/**
 * @constructor
 * Get all the child objects.
 * @param {string} opt_name Only return objects with this name
 */
pb.FieldObjectNode.prototype.getObjects = function (opt_name) {
    var children = [];
    goog.array.forEach(this.node.childNodes, function (node) {
        // Skip text nodes
        if (node.nodeType == 3) return;

        if (opt_name == null)
            return children.push(new pb.FieldObjectNode(node))

        if (opt_name.toLowerCase() == node.tagName.toLowerCase())
            return children.push(new pb.FieldObjectNode(node));
    });

    // Filter out undefined nodes
    return goog.array.filter(children, function (objectNode) {
        if (objectNode != undefined)
            return true;
    });
}

pb.FieldObjectNode.prototype.selectChildNode = function (node, childName) {

    if (goog.userAgent.IE || !!window.MSStream) {
        return goog.array.find(node.childNodes, function (childNode) {
            if (childNode.nodeName == childName)
                return true;
        }, this);
    } else {
        return goog.dom.xml.selectSingleNode(node, childName);
    }
};

/**
 * Get property value
 */
pb.FieldObjectNode.prototype.getPropertyValue = function (property) {
    property = property.toUpperCase();
    return this.node.getAttribute(property);
};

/**
 * Get node name
 */
pb.FieldObjectNode.prototype.getNodeName = function () {
    return this.node.nodeName;
};



/**
 * Find the deepest uc node, this represents the screen you are currently on in the XML Data Set.
 */
pb.FieldObjectNode.prototype.findDeepestUCNode = function () {
    var uc = this.getPropertyValue("UC");
    var childObjects = this.getObjects();

    for (var i = 0; i < childObjects.length; i++) {
        var result = childObjects[i].findDeepestUCNode();
        if (result != null) {
            return result;
        }
    }
    if (uc == "1") {
        return this;
    }
};

/**
 * Get parent UC node from existing uc node
 */
pb.FieldObjectNode.prototype.getParentUCNode = function () {
    if (this.getPropertyValue("UC") != "1")
        return null;

    return new pb.FieldObjectNode(this.node.parentNode);
};

/**
 * Get parent UC node from existing uc node
 */
pb.FieldObjectNode.prototype.getParentNode = function () {
    return new pb.FieldObjectNode(this.node.parentNode);
};


/**
 * Looks on the deepest uc level for the matching object property, if it doesn't find
 * it there, will look up a level, etc.
 */
pb.FieldObjectNode.prototype.getFieldValue = function (strObject, strProperty) {

    var object = this.findDeepestUCNode();
    while(object.node.nodeName != strObject.toUpperCase()) {
        object = object.getParentUCNode();
        if (object == null)
            return null;
    };

    // Look for the property
    return object.getPropertyValue(strProperty.toUpperCase())
};

