perseus.Lexeme = function (stream) {
    this.stream = stream;
    this["valueOf"] = this.valueOf;
    this.flags_ = this.calculateFlags_(stream);
    
};

/**
 * Operators
 */
perseus.Lexeme.operators = ["!", "not", "NOT", "&&", "and", "AND", "||",
    "or", "OR", "==", "=", "!=", "<>", ">", ">=", "<", "<=", "*", "/", "+",
    "-", ".", "(", ")", "[", "]"];

/**
 * Punctuators
 */
perseus.Lexeme.punctuators = ["(", ")", ",", "[", "]"];

/**
 * Keywords
 */
perseus.Lexeme.keywords = ["true", "false", "NOW", "now", "TODAY", "today", "null"];

/**
 * Lexme types, these can be ambiguous for example the opening bracket character
 * can be both a punctuator and operator. A punctuator when acting as 
 * parenthesis and an operator when invoking a method.
 * @enum
 */
perseus.Lexeme.Flags = {
    OPERATOR: 1,
    PUNCTUATOR: 2,
    KEYWORD: 4,
    IDENTIFIER: 8,
    LITERAL: 16
};

/**
 * @public
 * Gets the flags which match to lexeme types.
 */
perseus.Lexeme.prototype.getFlags = function () {
    return this.flags_;
};

/**
 * @private
 * Calculates the type flags.
 */
perseus.Lexeme.prototype.calculateFlags_ = function (stream) {
    var flags = 0;

    if (perseus.Lexeme.isLiteral(stream)) {
        flags = flags | perseus.Lexeme.Flags.LITERAL;
        // If it's a literal it can't be anything else
        return flags;
    }
    if (perseus.Lexeme.isKeyword(stream)) {
        flags = flags | perseus.Lexeme.Flags.KEYWORD;
    }
    if (perseus.Lexeme.isOperator(stream)) {
        flags = flags | perseus.Lexeme.Flags.OPERATOR;
    }
    if (perseus.Lexeme.isPunctuator(stream)) {
        flags = flags | perseus.Lexeme.Flags.PUNCTUATOR;
    }

    if ((flags & perseus.Lexeme.Flags.KEYWORD) 
        || (flags & perseus.Lexeme.Flags.OPERATOR)
        || (flags & perseus.Lexeme.Flags.PUNCTUATOR)) {
        // If it's a keyword, operator or punctuator then it can't
        // be an identifier.
        return flags;
    }

    if (perseus.Lexeme.isIdentifier(stream)) {
        flags = flags | perseus.Lexeme.Flags.IDENTIFIER;
    }

    return flags;
};


/**
 * Is a stream of characters a keyword
 * @param {string} stream
 * @returns {bool}
 */
perseus.Lexeme.isKeyword = function (stream) {
    if (stream == null)
        return false;
    return (goog.array.indexOf(perseus.Lexeme.keywords, stream) != -1) ? true : false;
};

/**
 * Is a stream of characters an operator
 * @param {string} stream
 * @returns {bool}
 */
perseus.Lexeme.isOperator = function (stream) {
    if (stream == null)
        return false;
    return (goog.array.indexOf(perseus.Lexeme.operators, stream) != -1) ? true : false;
};

/**
 * Is a stream of characters a punctuator
 * @param {string} stream
 * @returns {bool}
 */
perseus.Lexeme.isPunctuator = function (stream) {
    if (stream == null)
        return false;
    return (goog.array.indexOf(perseus.Lexeme.punctuators, stream) != -1) ? true : false;
};

/**
 * Is a stream of characters a literal
 * @param {string} stream
 * @returns {bool}
 */
perseus.Lexeme.isLiteral = function (stream) {
    if (goog.string.isNumeric(stream)) {
        return true;
    } else if (goog.string.countOf(stream, ".") == 1
        && goog.string) {
        var parts = stream.split(".");
        // 0. and .0 are valid but . is not
        if (parts[0].length == 0 && parts[1].length == 0)
            return false;
        if (goog.string.isNumeric(parts[0]) && goog.string.isNumeric(parts[1])) {
            return true;
        }
    }

    //var characters = new String(stream);
    var characters = stream.split('');
    if ((characters[0] == "'" || characters[0] == "\"")
        // Open and close quotes match
        && characters[0] == characters[characters.length -1]
        ) {


        // Keep on skipping over characters until the closing quote is found
        var i = 1;
        while (
            // Look for matching opening quote
            characters[i] != characters[0]
            // As long as the previous character wasn't an escape character
            && characters[i - 1] != "\\") {
            i++;
            
            if (i >= characters.length) {
                // TODO: Should we throw an unterminated string error here?
                return false;
            }
        }
        return true;
    }

    return false;
};

