/**
 * @private 
 * Gets the next token.
 * @returns {string}
 *
 */
perseus.Lexer.prototype.getNextToken_ = function () {

    // Check if we've reached the end of the stream, return null indicating
    // it's the end of tokens.
    if (this.currentCharacter == null) {
        this.tokensBuffer.push(null);
        return;
    }

    // Skip over any whitespace
    while (goog.string.isSpace(this.currentCharacter)) {
        this.getNextCharacter();
    }

    // Check for a punctuator, as all punctuators are single characters
    if (this.isPunctuator(this.currentCharacter)){
        this.tokensBuffer.push(this.currentCharacter);
        this.getNextCharacter();
        return;
    };


    // Check if is operator
    if (this.isOperatorChar(this.currentCharacter)) {
        // Peek ahead and check three characters for an operator
        var token = this.currentCharacter
            + this.peekCharacter(2)
            + this.peekCharacter(3);
        if (this.isOperator(token)){
            this.getNextCharacter();
            this.getNextCharacter();
            this.tokensBuffer.push(token);
            this.getNextCharacter();
            return;
        }

        // Peek ahead and check two characters for an operator
        token = this.currentCharacter
            + this.peekCharacter(2);
        if (this.isOperator(token)) {
            this.getNextCharacter();
            this.tokensBuffer.push(token);
            this.getNextCharacter();
            return;
        }

        // Check if char is operator
        if (this.isOperator(this.currentCharacter)){
            this.tokensBuffer.push(this.currentCharacter);
            this.getNextCharacter();
            return;
        }
    }

    var token = "";
    // Check if we are handling a string and consume until the end of the string
    if (this.currentCharacter == "\"" || this.currentCharacter == "'") {
        var terminatingChar = this.currentCharacter;
        var token = this.currentCharacter;
        // Consume characters until we find a terminating string character
        var escapeChar = null;
        this.getNextCharacter();
        while (this.currentCharacter != null
                && (this.currentCharacter != terminatingChar/* ||
                (this.currentCharacter == terminatingChar
                    && escapeChar == "\\")*/)) {
            escapeChar = this.currentCharacter;
            // If the current character was escaped then remove the escape
            // slash
            if (this.currentCharacter == "\\" 
                && this.peekCharacter(2) == terminatingChar) {
                this.currentCharacter = this.getNextCharacter();
            }

            token = token + this.currentCharacter;
            this.getNextCharacter();
        }
        token = token + this.currentCharacter;
        this.getNextCharacter();
        this.tokensBuffer.push(token);
        return;
    }

    // Find identifiers
    while (this.currentCharacter != null
        && (!goog.string.isSpace(this.currentCharacter))
        && (!this.isPunctuator(this.currentCharacter))
        // Must be alpha numeric or underscore
        && (goog.string.isAlphaNumeric(this.currentCharacter)
            || this.currentCharacter == "_")
        ) {

        token = token + this.currentCharacter;
        this.getNextCharacter();
    }

    if (token == "") {
        this.tokensBuffer.push(null);
        return;
    }

    this.tokensBuffer.push(token);
    return;

};


/**
 * @public
 */
perseus.Lexer.prototype.getNextCharacter = function () {
    this.limit--;
    if (this.limit == 0)
    {
        // Prevents infinite looping if an error is encountered
        throw "Internal error, lexer stuck in infinite loop.";
    }
        
    if (this.index >= this.stream.length) {
        this.currentCharacter = null;
    } else {
        this.currentCharacter = this.stream.charAt(this.index);
    }
    this.index++;
    return this.currentCharacter;
};


/**
 * Is a character a punctuation character?
 */
perseus.Lexer.prototype.isPunctuator = function (charS) {
    return (goog.array.indexOf(this.punctuators, charS) != -1);
};


/**
 * Peek at the next tokens. Does not alter the internal character pointer.
 * @param {number} index For the next token use index 1, the token after would
 * be 2, etc.
 */
perseus.Lexer.prototype.peekToken = function (index) {
    if (index == 0) return this.previousToken;

    while (this.tokensBuffer[this.tokensBuffer.length - 1] !== null 
        && this.tokensBuffer.length < index) {
        this.getNextToken_();

    }

    return this.tokensBuffer[index - 1] || null;
};

/**
 * If we match an operators first character return true
 */
perseus.Lexer.prototype.isOperatorChar = function (charS) {
    return (goog.array.indexOf(this.operatorsFirstChar, charS) != -1);
};

/**
 * If we match an operators first character return true
 */
perseus.Lexer.prototype.isOperator = function (operator) {
    return (goog.array.indexOf(this.operators, operator) != -1);
};

/**
 * Peek at the next character. Does not alter the internal character pointer.
 * @param {number} index For the next character use index 1, the token after
 * would be 2, etc. Can also peek chacters before the current character.
 */
perseus.Lexer.prototype.peekCharacter = function (index) {

    index = this.index
        // Because the pointer is currently pointing at the next character
        - 2
        + index;

    if (index >= this.stream.length) {
        return null;
    } else if (index < 0) {
        return null;
    } else {
        return this.stream.charAt(index);
    }
};

/**
 * Get the original string this lexer is parsing
 * @return {string}
 */
perseus.Lexer.prototype.getRawString = function(){
    return this.stream;
};
