titan.date.Difference = function (fromDate, toDate) {
    this.fromDate = fromDate || new Date();
    this.toDate = toDate || new Date();
};


/**
 * Get the number of years between the dates
 */
titan.date.Difference.prototype.getYears = function () {
    var temp = this.toDate.getFullYear() - this.fromDate.getFullYear();

    // Check if a full year hasn't passed
    if (this.fromDate < this.toDate &&
        (this.fromDate.getMonth() > this.toDate.getMonth()
        || this.fromDate.getMonth() == this.toDate.getMonth() && this.fromDate.getDate() > this.toDate.getDate())) {
        temp--;
    } else if (this.fromDate > this.toDate &&
        (this.fromDate.getMonth() < this.toDate.getMonth()
        || this.fromDate.getMonth() == this.toDate.getMonth() && this.fromDate.getDate() < this.toDate.getDate())) {
        temp++;
    }

    return temp;
}

/**
 * Get the number of months between the dates.
 */
titan.date.Difference.prototype.getMonths = function () {
    var temp = this.toDate.getMonth() -
            this.fromDate.getMonth() +
            (12 * (this.toDate.getFullYear() - this.fromDate.getFullYear()));

    // Check if a full month hasn't passed
    if (this.fromDate < this.toDate &&
        (this.fromDate.getDate() > this.toDate.getDate())) {
        temp--;
    } else if (this.fromDate > this.toDate &&
        (this.fromDate.getDate() < this.toDate.getDate())) {
        temp++;
    }

    return temp;
};

/**
 * Get the number of days between the dates
 */
titan.date.Difference.prototype.getDays = function () {

    var from = new Date(this.fromDate.getYear(), this.fromDate.getMonth(), this.fromDate.getDate());
    var to = new Date(this.toDate.getYear(), this.toDate.getMonth(), this.toDate.getDate());

    from.setMinutes(from.getMinutes() - from.getTimezoneOffset());
    to.setMinutes(to.getMinutes() - to.getTimezoneOffset());

    return (to - from) / (24 * 60 * 60 * 1000);
};


