/***/
perseus.functions.GetServerVal = function(nameOfServerFunction) {


    var queryValues = [];
	
    for (var i = 1; i < arguments.length; i++) {
        var value = arguments[i];
        /*if (arguments[i].getValue){
            value = arguments[i].getValue();
        }*/
		
        if (value instanceof pb.fields.XmlDataSet 
            && (typeof value.getValue()) == "string") {
            // It may be a date, try and parse it as such
            var localV = value.getValue();
            value = Date.parseString(localV) || localV;
        } else if (value instanceof pb.fields.AbstractBase) {
            value = value.getValue();
        }
        if (value instanceof Date) {
            value = value["format"]("dd/MM/yyyy");
        } else if (value == null) {
            value = "";
        } else if ((typeof (value)) == "object") {
            value = value.valueOf();
        } else {
            value = value.toString();
        }
        queryValues.push(value);
    }

    // The query string will take the form, the first 8 characters will define
    // how many of the next characters make up the parameter. For example to
    // send the value 'dog' the query string would look like
    // 00000003dog
    // 
    // The non encoded query string to two parameters 'dog' and the 
    // 'the quick brown fox' would be
    // 00000003dog00000019the quick brown fox
    var queryString = "";
    goog.array.forEach(queryValues, function (queryValue) {
        var length = queryValue.length + "";
        // Pad length with zeros up to 8 characters
        if (length.length > 8) {
            throw "Parameter value to large to send.";
        }
        while (length.length < 8) {
            length = "0" + length;
        }
        queryString += length + queryValue;
    }, this);
	
    var url = window.location.href.split('?')[0] + "?GetValFor_" + nameOfServerFunction + "=" + queryString;
    var xmlDoc = perseus.functions.GetServerValCache[url];
    if (!xmlDoc) {
        var request = new XMLHttpRequest();
        request.open("GET", url, false);
        request.send(null);

        if (request.status === 200 && request.responseXML) {
            var xmlDoc = request.responseXML.documentElement;

            perseus.functions.GetServerValCache[url] =
                xmlDoc;
        } else {
            throw new Error("Request failed: " + url);
            return null;
        }
        
        /*return new Promise(function(resolve, reject){
            var xhr = new goog.net.XhrIo();
            goog.events.listen(xhr, "complete", function(e){
                if (xhr.isSuccess()){
                    var xmlDoc = xhr.getResponseXml();
                    perseus.functions.GetServerValCache[url] = xmlDoc;
                    resolve(xmlDoc);
                } else {
                    reject("Request failed:" + url);
                }
            });
            xhr.send(url);
        });*/
    }
    if (xmlDoc) {
        var results = xmlDoc.getElementsByTagName("Res");

        for (var i = 0; i < results.length; i++) {
            var val = results[i].getAttribute("val");

            return val.toString();
        }
    }
    return null;
};


