/***/
perseus.functions.AjaxifyRateList = function (fields, listNames, opt_effectiveDate) {
    // Assumes the first field has been populated from the server


    goog.array.forEach(fields, function (field, i) {
        // Rate controls by default refresh the page on change, this turns
        // that feature off
        field.unlistenOnRateControl();
        // Field also needs to be enabled by default
        field.setReadOnly(false);

        // Listen for the change event
        goog.events.listen(field, "change", function () {
            goog.array.forEachRight(fields, function (field, j) {
                // Comes after the next field of the field changed
                if (j > i + 1) {
                    field.clearOptions();
                } else if (j == i + 1) {
                    // Is the next field
                    // Populate the field filtered by the parent
                    field.offDocument();
                    var options = perseus.functions.GetList(listNames[j],
                        // If getValue returns an empty string '' then that 
                        // means the parent has 'please select' selected. 
                        // So we'll specifically set the parentId to 0 to ensure
                        // zero child records are returned, else all records
                        // for this field would be returned.
                        fields[i].getValue() || '0', 
                        opt_effectiveDate);
                    field.setOptions(options);
                    // Select the first option if only one is available
                    if (options.length == 1) {
                        field.setValue(options[0].value);
                    }
                    field.onDocument();
                } else {
                    // List is above or is the current field, so do not
                    // change
                }
            }, this);
        }, false, this);
    }, this);


};

/**
 * Toggles an element to be displayed or not
 * @param {Element|string} Accepts an element or an element's id
 */
perseus.functions.ToggleElementDisplay = function (element) {
    if (typeof (element) == "string")
        element = goog.dom.getElement(element);

    if (goog.dom.classes.has(element, "hidden")) {
        goog.dom.classes.remove(element, "hidden");
    } else {
        goog.dom.classes.add(element, "hidden");
    }
};

/**
 * Sets an element to display
 * @param {Element|string} Accepts an element or an element's id
 * @param {bool} value
 */
perseus.functions.SetElementDisplay = function (element, value) {
    
    if (typeof (value) == "object")
        value = value.valueOf();

    // Parse the value if it is a string number
    if ((typeof value) == "string" && goog.string.isNumeric(value)) {
        value = !!window["parseInt"](value);
    }

    if (typeof (element) == "string")
        element = goog.dom.getElement(element);

	// Don't do anything if the element isn't accessible
	if (element == null)
		return;
		
    // Treat null as false
    if (value == null) value = false;

    // Make the element to toggle off the li element if it is the parent
    if (element.parentNode.tagName.toUpperCase() == "LI")
        element = element.parentNode;

    if (value.valueOf()) {
        goog.dom.classes.remove(element, "hidden");
    } else {
        goog.dom.classes.add(element, "hidden");
    } 
};


