/**
 * Check if a node in the xml data set has a unique value with the same type
 * of nodes one level under the parent.
 */
perseus.functions.IsUnique = function (field) {
    var value = field.getValue();

    var root = new XMLDataSetReader(window["XMLDataSet"]);
    var uc = root.findDeepestUCNode();
    // Make sure we are working on the current level
    if (uc != null) {
        root = uc.getParentUCNode();
    }

    return !goog.array.some(root.getObjects(), function (childNode) {
        // If the node is the current node then don't check against itself
        if (childNode.getPropertyValue("UC") == "1") return false;

        if (childNode.getNodeName() != field.getObjectName()) return false;

        return (childNode.getPropertyValue(field.getPropertyName()) == value)
    });
};


