/**
 * Round a number
 * @param {number} value The number to be rounded.
 * @param {integer} opt_numberDigits The number of digits precision to round to.
 */
perseus.functions.Round = function (value, opt_numberDigits) {
    if (typeof (value) == "string") {
        value = window["parseFloat"](value);
    }

    if (opt_numberDigits == null) {
        opt_numberDigits = 0;
    }
    var factor = Math.pow(10, opt_numberDigits);

    value = value * factor;

    value = Math.round(value);
    value = value / factor;

    return value;
};


/**
 * Round a number down
 * @param {number} value The number to be rounded.
 * @param {integer} opt_numberDigits The number of digits precision to round to.
 */
perseus.functions.Floor = function (value, opt_numberDigits) {
    if (typeof (value) == "string") {
        value = window["parseFloat"](value);
    }

    if (opt_numberDigits == null) {
        opt_numberDigits = 0;
    }
    var factor = Math.pow(10, opt_numberDigits);

    value = value * factor;

    value = Math.floor(value);
    value = value / factor;

    return value;
};


/**
 * Round a number up
 * @param {number} value The number to be rounded.
 * @param {integer} opt_numberDigits The number of digits precision to round to.
 */
perseus.functions.Ceil = function (value, opt_numberDigits) {
    if (typeof (value) == "string") {
        value = window["parseFloat"](value);
    }

    if (opt_numberDigits == null) {
        opt_numberDigits = 0;
    }
    var factor = Math.pow(10, opt_numberDigits);

    value = value * factor;

    value = Math.ceil(value);
    value = value / factor;

    return value;
};

/**
 * Adds text to a label field
 * @param {Element|id} The element or element id or a field
 * @param {string} text The text to add.
 */
perseus.functions.AddTextToLabel = function (element, text) {

    if (element instanceof pb.fields.AbstractBase) {
        element.addText(text);
        // Need to return true so they can be chained with &&
        return true;
    }

    if ((typeof element) == "string") {
        var id = element;
        element = document.getElementById(id);
        if (!element) throw "Label with id '" + id + "' not found.";
    }

    // TODO Do something about desposing this object that has been created for
    // one time use.
    var label = new pb.fields.Label(null, element.id);
    label.decorate(element);

    label.addText(text);

    // Return true so that using && can do multiple statements
    return true;
};

