/**
 * Returns -1 if date1 comes before date2. 0 if they are the same date else
 * 1. 
 * @param {Date} date1
 * @param {Date} date2
 * @return {number}
 */
perseus.functions.DateCompare = function (date1, date2) {
    
    // There's a bug in IE that crashes the page when one of the dates is 
    // a field object that's value is null. So have to break the simple compare
    // method down into two steps, first getting the value then comparing it.
    date1 = date1.valueOf();
    date2 = date2.valueOf();

    if (date1 < date2) return -1;
    if (date1 > date2) return 1;
    return 0;
};

