/**
 * Perform a boolean query for every child object, if all queries return true
 * the result returns true.
 * @param {Array} childObjects An array of child objects
 * @param {string} subExpression An expression to evaluate against each child
 * object.
 * e.g. of being part of a normal expression
 * "EveryChild(RECALC.RECSOLITEM, 'RECSOLITEM.RECTYPECODE == \'YES\')"
 */
perseus.functions.EveryChild = function (childObjects, subExpression)
{
    if (childObjects == null)
        childObjects = [];
	
	childObjects = childObjects.valueOf();

    if (childObjects == null)
        childObjects = [];
	
    return goog.array.every(childObjects, function (childNode)
        {
            var evaluator = new perseus.Evaluator(subExpression, {
                // Provide a context and override the getIdentifier method
                getIdentifier: function (token)
                {
                    var value = token.valueOf();
                    if (value.indexOf(".") != -1)
                    {
                        var parts = value.split(".");
                        if (parts[0].toUpperCase() == childNode.getName())
                            return childNode.getAttribute(parts[1]) 
                                // Treat no record of the value as 0
                                || 0;
                    }
                    // Use the original get identifier function
                    return perseus.Evaluator.prototype.getIdentifier.call(this, token);
                }
            });
            return evaluator.valueOf();
        }
    );
};

/**
 * Queries an array of child objects and sums the value returned for each
 * query
 * @param {Array} childObjects An array of child objects
 * @param {string} subExpression An expression to evaluate against each child
 * object. The value returned from this will be summed
 */
perseus.functions.SumChildren = function (childObjects, sumExpression, 
    whereExpression)
{
    if (childObjects == null)
        childObjects = [];
    var total = 0;
	
	childObjects = childObjects.valueOf();

    if (childObjects == null)
        childObjects = [];

    

    goog.array.forEach(
        childObjects, 
        function (childNode) {

            var evalParamObj = {
                // Provide a context and override the getIdentifier method
                getIdentifier: function (token)
                {
                    var value = token.valueOf();
                    if (value.indexOf(".") != -1)
                    {
                        var parts = value.split(".");
                        if (parts[0].toUpperCase() == childNode.getName())
                            return childNode.getAttribute(parts[1]) 
                                        // Treat no record of the value as 0
                                        || 0;
                    }
                    // Use the original get identifier function
                    return perseus.Evaluator.prototype.getIdentifier.call(this, token);
                }
            };

            var evaluator = new perseus.Evaluator(sumExpression, evalParamObj);
            var expValue = evaluator.valueOf();
            if (perseus.functions.IsNumeric(expValue)){
                expValue = goog.string.toNumber(expValue);
            }
            if (whereExpression != null)
            {
                if ((new perseus.Evaluator(whereExpression, evalParamObj)).valueOf())
                {
                    // If it can be treated as numeric then treat it as so
                    try {
                        expValue = window["parseFloat"](expValue);
                    } catch (e){
                        //
                    }
                    total += expValue;
                }
            } else
            {
                total += expValue;
            }
        }
    );

    return total;
};

/**
 * Returns the result with the highest value
 * @param {Array} childObjects An array of child objects
 * @param {string} returnExpression The value to return from the max value
 * child object
 * @param {string} subExpression An expression to evaluate against each child
 * object. The value from this is used to evaluate the max value
 * @param {string} whereExpression Only child objects that return true for
 * this expression are counted
 * e.g. of being part of a normal expression
 */
perseus.functions.MaxChild = function (childObjects, returnExpression, subExpression, whereExpression)
{
    if (childObjects == null)
        childObjects = [];
	
	childObjects = childObjects.valueOf();

    if (childObjects == null)
        childObjects = [];
	
	var maxValue = 0;
    var returnValue = '';
    goog.array.forEach(childObjects, function (childNode)
        {
            var evalParamObj = {
                // Provide a context and override the getIdentifier method
                getIdentifier: function (token)
                {
                    var value = token.valueOf();
                    if (value.indexOf(".") != -1)
                    {
                        var parts = value.split(".");
                        if (parts[0].toUpperCase() == childNode.getName())
                            return childNode.getAttribute(parts[1]) 
                                // Treat no record of the value as 0
                                || 0;
                    }
                    // Use the original get identifier function
                    return perseus.Evaluator.prototype.getIdentifier.call(this, token);
                }
            };
            
            if (whereExpression != null)
            {
                if (!(new perseus.Evaluator(whereExpression, evalParamObj)).valueOf())
                {
                      return;
                }
            } 


            var val = (new perseus.Evaluator(subExpression, evalParamObj)).valueOf();

            if (val < maxValue)
                return

			maxValue = val;
            returnValue = (new perseus.Evaluator(returnExpression, evalParamObj)).valueOf();

			
        }
    );
	return returnValue;
};

