/**
 * Queries an array of child objects and hides the table links on a child
 * screen for each array value
 * @param {object} field The inline child field
 * @param {Array} childObjects An array of child objects
 * @param {Array<string>} captionsToHide An array of caption names to hide
 * @param {string} whereExpression An expression to evaluate against each child
 * object. If true is returned then the captions will be hidden
 */
perseus.functions.HideChildLinks = function (field, childObjects, 
  captionsToHide, whereExpression)
{
  
  if (typeof field == 'string')
    field = Field.getInstance(field)

  if (childObjects == null)
    childObjects = [];
  
  childObjects = childObjects.valueOf();

  if (childObjects == null)
    childObjects = [];

  var links = []
  var ls = goog.dom.query("a", field.getElement());

  // Sort links into a multi dimensional array so each element
  // matches the links for that child object
  var linksPerChild = Math.round(ls.length / childObjects.length)
  goog.array.forEach(ls, function(link, i){
    var k = Math.floor(i / linksPerChild)
    links[k] = links[k] || [] 
    links[k].push(link)
  })
  


  goog.array.forEach(
    childObjects, 
    function (childNode, index) {

      var evalParamObj = {
        // Provide a context and override the getIdentifier method
        getIdentifier: function (token)
        {
          var value = token.valueOf();
          if (value.indexOf(".") != -1)
          {
            var parts = value.split(".");
            if (parts[0].toUpperCase() == childNode.getName()){
              var token = childNode.getAttribute(parts[1])
              if (token == null)
                return new perseus.keyword.Null
              return token
            }
          }
          // Use the original get identifier function
          return perseus.Evaluator.prototype.getIdentifier.call(this, token);
        }
      };

      if (whereExpression != null)
      {
        if ((new perseus.Evaluator(whereExpression, evalParamObj)).valueOf())
        {
          hideLinks(links[index], true)
        } else {
          hideLinks(links[index], false)
        }
      } else
      {
        hideLinks(links[index], true)
      }
    }
  );

  function hideLinks(links, enable){
    goog.array.forEach(links, function(link){
      /*
      */
      // Hide specified links
      var linkCaption = link.innerHTML;
      // Trim
      linkCaption = linkCaption.replace(/^\s+|\s+$/g, '');
      if (goog.array.some(captionsToHide, function(validLinkCaption){
        return ((validLinkCaption + "").toLowerCase() == linkCaption.toLowerCase());
      })){
        if (enable)
          link.style.display = "none";
        else     
          link.style.display = "inline";
      }
      
    });
  }
};
/**
 * Used to group a series of tokens into a FunctionCall.
 */

goog.provide('perseus.symbol.FunctionCall');
goog.require('perseus.Symbol');

goog.require('perseus.functions');
goog.require('perseus.asynFunctions');

/**
 * @constructor
 */
perseus.symbol.FunctionCall = function () {
    goog.base(this);
};
goog.inherits(perseus.symbol.FunctionCall, perseus.Symbol);


/**
 * Get the value
 */
perseus.symbol.FunctionCall.prototype.valueOf = function () {
    var context = null;

    // This is for object method calls
    var func = this[0].valueOf();
    if (func instanceof Array) {
        // Allow operators to return both the function and the context of the
        // function to be called in.
        context = func[1];
        func = func[0];
    }

    // This is for global functions
    if (!(func instanceof Function))
        func = perseus.functions[func];


    if (!(func instanceof Function))
        throw "Function '" + this[0].valueOf() + "' not found.";

    var args = [];
    for (var i = this.length - 1; i >= 1; i--) {
        var value = this[i];
        if (value instanceof perseus.Symbol || value instanceof perseus.Token)
            value = this[i].valueOf();
        args.push(value);
    }

    return func.apply(context, args);
};

/**
 * Get the value asynchonously
 */
perseus.symbol.FunctionCall.prototype.getAsynchronousValue =
function (callback) {
    var self = this;
    
    var args = [];
    for (var i = this.length - 1; i >= 1; i--) {
        var value = this[i];
        if (value instanceof perseus.Symbol || value instanceof perseus.Token)
            value = this[i].valueOf();
        args.push(value);
    }

    var doGetValue = function () {
        var func = perseus.asyncFunctions[self[0].valueOf()];
        if (func) {
            func.apply(null,
                goog.array.concat(
                    [callback], args
                )
            );
            return;
        } 
        // We couldn't find an asynchronous function so look for a normal 
        // function
        func = perseus.functions[self[0].valueOf()];
        if (func) {
            var value = func.apply(null, args);
            callback(value);
            return;
        }

        throw "Function '" + self[0].valueOf() + "' not found.";
    };

    // Need to get the value of all the args values
    var total = args.length;
    goog.array.forEach(args, function (arg, index) {
        if (!arg.getAsynchronousValue) {
            total--;
            if (total == 0)
                doGetValue();
            return;
        }
        arg.getAsynchronousValue(function (value) {
            args[index] = value;
            total--;
            if (total == 0)
                doGetValue();
        });
    })
};

