/**
 * @constructor
 * @param {string|String|perseus.Lexer}
 */
perseus.Parser = function (streamOrLexer) {

    this["valueOf"] = this.valueOf;

    if ((typeof streamOrLexer) == "string" || streamOrLexer instanceof String) {
        // Remove tabs
        streamOrLexer = goog.string.normalizeSpaces(streamOrLexer);
        this.lexer = new perseus.Lexer(streamOrLexer);
    } else {
        // Assume it's a lexer
        this.lexer = streamOrLexer;
    }


    // Tracks unterminated braces and the different types, i.e. method invokes
    // and parenthesis.
    // Moved over from Lexer.js now that this class figures out the context
    this.bracesStack = [];

    // Keep a record of the fields used in this expression so they can be 
    // quickly retrieved if required later.
    this.fields = null; // Will become an array after being evaluated.
};

