/**
 * Convert to postfix
 * @private
 */
perseus.Parser.prototype.getParseTree = function () {
    // All the fields within the expression found.
    this.fields = [];
    this.operators = [];
    this.operands = [];

    // The key of the object will match the index of open parenthesis on the
    // this.operators stack that indicates a function call. The values will be
    // the function parameters.
    this.functionParamMapping = [];

    this.arrayDeclarationParamMapping = [];

    /*var tokeniser = new titan.lex.Tokeniser(
        stream,
        this.this.operators,
        this.punctuators,
        this.caseSensitive
    );*/

    this.previousToken = null;
    var token = this.getNextToken();
    // The actual token before it is parsed to a simple type, 
    // e.g. processLiteral
    var originalToken = token;

    while (token != null) {


        //goog.array.forEach(tokens, function (token) {

        if (token instanceof perseus.token.Operator) {
            this.nextOperator(token);
        } else if (token instanceof perseus.token.Punctuator){

            this.nextPunctuator(token);
        
        } else if (token instanceof perseus.token.Keyword) {
            
            this.nextKeyword(token);
            
        }  else {
            // Is not an operator or parenthesis, is a value
            if (token instanceof perseus.token.Identifier) {
                token = this.getIdentifier(token);
            } else if (token instanceof perseus.token.Literal) {
                token = this.processLiteral(token);
            }
            this.operands.push(token);
        }

        this.previousToken = originalToken;
        token = this.getNextToken();
        originalToken = token;
        
        // Some validation
        if (this.previousToken instanceof perseus.token.Identifier &&
            token instanceof perseus.token.Identifier) {
            throw "Invalid token sequence, cannot follow an identifier with " +
                "another identifier.";
        }
    };

    this.shrinkOperatorStack();

    return this.operands[0];
};


/**
 * Does operator1 have higher or equal precendence than operator 2.
 * @private
 * @returns {boolean}
 * @param {perseus.token.Operator} operator1
 * @param {perseus.token.Operator} operator2 This is the current token
 */
perseus.Parser.prototype.higherOrEqualPrecedence = function (operator1, operator2) {
    /*if (operator1.unary == true && operator2.binary == true)
        return true;
    else if (operator1.binary == true && operator2.unary == true)
        return false;*/
    if (operator1.valueOf() == "!"
        && operator2.valueOf() == "!") return false;
    
    if (operator1.valueOf() == operator2.valueOf() && (
            (operator1.unary == true && operator2.unary == true)
            || (operator1.binary == true && operator2.binary == true)
        ))
        return true;

    return this.getPrecedenceLevel(operator1) >= this.getPrecedenceLevel(operator2);
};


/**
 * @private
 */
perseus.Parser.prototype.getIdentifier = function (token) {
    

    if (perseus.identifiers[value] != undefined) {
        // TODO This may change on time of evaluation so don't parse back
        // the actual value yet
        return new perseus.Identifier(value);
    } 
    
    // Else keep the identifier token
    return token;

    // Unknown identifier
    //throw token.valueOf() + " is not defined";
    // ^ Won't work when identifier is a method
};




/**
 * @private
 */
perseus.Parser.prototype.processLiteral = function (token) {
    var value = token.valueOf();

    // We want to preserve any back slashes on strings, that eval will strip 
    // out. So we'll manually check for start and end slashes, and return what's
    // between them.
    if ((value.slice(0, 1) == "'" && value.slice(-1) == "'")
        || (value.slice(0, 1) == "\"" && value.slice(-1) == "\"")) {
        return value.slice(1, -1);
    }

    return eval(value);
};


/**
 * Gives a number to an operator indicating it's precedence level.
 * The higher the number the higher the precedence.
 * @private
 * @param {String} operator
 * @return {Integer}
 */
perseus.Parser.prototype.getPrecedenceLevel = function (operator) {
    // Alias for precedence constructor
    var p = perseus.Parser.PrecedenceLevel;

    var precedenceLevels = [
        [new p(".", 2), new p("("), new p(")"), new p("["), new p("]")],
        [new p("+", 1), new p("-", 1), new p("!", 1), new p("not", 1), new p("NOT", 1)],
        [new p("*", 2), new p("/", 2)],
        [new p("+", 2), new p("-", 2)],
        [new p(">", 2), new p(">=", 2), new p("<", 2), new p("<=", 2)],
        [new p("==", 2), new p("!=", 2), new p("=", 2), new p("<>", 2)],
        [new p("&&", 2), new p("and", 2), new p("AND", 2)],
        [new p("||", 2), new p("or", 2), new p("OR", 2)]
    ];


    // If operator is a punctuator bracket, give it a lower precedence
    if (((typeof(operator) == "string")
        || (operator instanceof perseus.token.Punctuator))
        && (operator.valueOf() == "("
            || operator.valueOf() == ")")) {
        return precedenceLevels.length;
    }

    var levelIndex = goog.array.findIndex(precedenceLevels, function (level, index) {
        // level is type {perseus.Parser.PrecedenceLevel}
        if (goog.array.find(level, function(precedenceLevel){
            if (operator.valueOf() == precedenceLevel.token
                && ((precedenceLevel.isUnary() && operator.unary == true)
                    || (precedenceLevel.isBinary() && operator.binary == true)
                    || (precedenceLevel.isUnary() == false && precedenceLevel.isBinary() == false)))
            return true;
        }, this)) return true;
    });
    if (levelIndex == -1)
        throw "Unknown operator.";

    return precedenceLevels.length - levelIndex;
};


/**
 * The next token is an operator.
 * @param {perseus.token.Operator}
 */
perseus.Parser.prototype.nextOperator = function (token) {
    //if (token.valueOf() == "(") {
    //    this.functionParamMapping[this.functionParamMapping.length] = [];
    //    this.operators.push(token);
    //    return;
    //}





    var topOfStackIsMethodCall = this.operators.length > 0 && 
        this.operators[this.operators.length - 1].valueOf() == "(";


    // Flag the token as unary or binary
    // TODO Make flagging a bit more graceful

    if (this.previousToken instanceof perseus.token.Punctuator
        && this.previousToken.valueOf() == ",") {
        token.unary = true;
    } else if (token.valueOf() != "(" && token.valueOf() != ")") {
        if (this.previousToken == null ||
            (this.previousToken instanceof perseus.token.Operator
                && this.previousToken.valueOf() != ")")) {
            token.unary = true;
        } else if (
            token.valueOf() == "!"
            || token.valueOf() == "!"
            || token.valueOf() == "not"
            || token.valueOf() == "NOT") {
            token.unary = true;
        } else {
            token.binary = true;
        }
    }

    if (!topOfStackIsMethodCall) {







        // There exists an operater at the top of the stack with higher or
        // equal precedence to the current operator
        var existsHigherOperator =
            (this.operators[this.operators.length - 1] instanceof perseus.token.Operator)
            // The token has less than or equal precendence to the top of the this.operators token
            && this.higherOrEqualPrecedence(
                this.operators[this.operators.length - 1],
                token);


        // There is an operator at the top of the stack
        if (existsHigherOperator) {
            // If previous token is an operator this is a unary operator, else a binary operator

            this.popOperatorStack();
        }
        /*} else if (
            // Previous token is operator
            (goog.array.peek(this.operators) instanceof perseus.token.Operator
                // Top of stack is not method call
                && this.operators[this.operators.length - 1].valueOf() == "(")
                // This is the first token
                || this.previousToken == null){
            // Unary operator
            // Assume it's right to left associative.
            
            //parseTree = new perseus.symbol.UnaryOperation();
            //parseTree.push(this.operators.pop(), this.operands.pop());
        }*/
    }



    if (token.valueOf() == "(")
    {
        var funcArray = [];
        funcArray.operandIndex = this.operands.length;
        this.functionParamMapping[this.functionParamMapping.length] = funcArray;
    } else if (token.valueOf() == ")")
    {
        // This is the closing bracket for a function
        // If the previous token was not an opening bracket, then
        // the top of the operands is the last argument to the function
        // call
        
        
        // Evaluate everything down to the method invocation bracket to add a new param
        this.shrinkOperatorStack("(");
        
        
        var funcArray = goog.array.peek(this.functionParamMapping);
        if (this.operands.length > funcArray.operandIndex)
        {
            
            funcArray.push(this.operands.pop());
        }
        
        this.popFunctionStack();
        
        
        // Discard the opening bracket
        this.operators.pop();
        
        // Return because the parseTree was added inside of popFunctionStack
        return;
    } /*else if (token.valueOf() == "]")
    {
        // Evaluate everything down to the method invocation bracket to add a new param
        this.shrinkOperatorStack("[");
        var value = this.operands[this.operands.length - 2][this.operands.length - 1];
        this.operands.pop();
        this.operands.pop();
        this.operands.push(value);
    }*/



    this.operators.push(token);
};


/**
 * The next token is a punctuator.
 */
perseus.Parser.prototype.nextPunctuator = function (token) {
    if (token.valueOf() == ")") {
                
        // Add all the values on the operators to the operands, operators
        // that come before an opening bracket or the start of the operators
        /*while (this.operators[this.operators.length - 1] != "(") {
            // Assume it's a binary operator
            var temp = new perseus.symbol.BinaryOperation();
            temp.push(this.operators.pop(), this.operands.pop(), this.operands.pop());
            this.operands.push(temp);
            if (this.operators.length == 0) {
                // We were expecting to find an opening bracket 
                var error = new Error("Miss matched parenthesis in expression, expecting an opening bracket");
                throw error;
            }
        }*/

        this.shrinkOperatorStack("(");
                
        // Discard the opening bracket
        this.operators.pop();
    }
    if (token.valueOf() == "(") {
        this.operators.push("(");
    }
    if (token.valueOf() == ",") {

        // Evaluate this.operators until the method invocation is at the top 
        // of the stack or the array declaration
        var topOperator = goog.array.peek(this.operators);
        while (topOperator.valueOf() != "(" && topOperator.valueOf() != "[") {
            this.popOperatorStack();
            topOperator = goog.array.peek(this.operators);
        }
        //this.shrinkOperatorStack("(", "[");

        // TODO put this somewhere else

        // If top of stack is a bracket this is a function param
        if (topOperator.valueOf() == "(") {
            goog.array.peek(this.functionParamMapping).push(this.operands.pop());
        } else if (topOperator.valueOf() == "[") {
            // Top operator is a bracket
            if (topOperator instanceof perseus.token.Punctuator) {
                // Array declartion
                goog.array.peek(this.arrayDeclarationParamMapping).push(this.operands.pop());
            } else if (topOperator instanceof perseus.token.Operator) {
                // Not implemented yet
            }
        }
    }
    if (token.valueOf() == "[") {
        // Creating new array
        this.arrayDeclarationParamMapping[this.arrayDeclarationParamMapping.length] = [];

        // Add to operators
        this.operators.push(token);

    }
    if (token.valueOf() == "]") {
        // This is the closing brace for an array declaration
        // If the previous token was not an opening brace, then
        // the top of the operands is the last argument to the declaration

        if ((!(this.previousToken instanceof perseus.token.Operator))
            || this.previousToken.valueOf() != "[") {
            goog.array.peek(this.arrayDeclarationParamMapping).push(this.operands.pop());
        }



        this.popArrayDeclarationStack();


        if (this.operators.length == 0)
        {
            var error = new Error("Mismatched parentheses in expression: " +
                this.lexer.stream.valueOf());
            if (error.display) error.display();
            throw error;
        }
        // Discard the opening brace
        this.operators.pop();


        return;

    }
};



/**
 * The next token is a keyword.
 */
perseus.Parser.prototype.nextKeyword = function (token) {
    // Hard code some keyword handling for now
    // TODO make it more manageable
    var value = token.valueOf();
    if (value == "true")
        this.operands.push(true);
    else if (value == "false")
        this.operands.push(false);
    else if (value == "NOW" || value == "now")
        this.operands.push(new perseus.keyword.Now());
    else if (value == "TODAY" || value == "today")
        this.operands.push(new perseus.keyword.Today());
    else if (value == "null")
        this.operands.push(new perseus.keyword.Null());
    else
        throw "Unsupported keyword '" + value + "'";
};


/**
 * Get the parse tree
 */
perseus.Parser.prototype.valueOf = function () {
    if (this.value == null) {
        this.value = this.getParseTree();
    }

    return this.value;
};

/**
 * Evaluate the operators and operands until we
 * run out of operators or reach the operator to stop at.
 * @param {string} opt_haltOperator
 */
perseus.Parser.prototype.shrinkOperatorStack = function (opt_haltOperator) {
    while (this.operators.length > 0
        && ((opt_haltOperator) ? goog.array.peek(this.operators).valueOf() != opt_haltOperator : true)
        ) {
        
        this.popOperatorStack();
    }
};


/**
 * Pops the top set of arguments off the function array and adds a function 
 * parse tree to the operands stack.
 */
perseus.Parser.prototype.popFunctionStack = function () {
    // Add our function call
    var parseTree = new perseus.symbol.FunctionCall();
    parseTree.push(this.operands.pop());
    while (goog.array.peek(this.functionParamMapping).length > 0) {
        parseTree.push(goog.array.peek(this.functionParamMapping).pop());
    };
    this.operands.push(parseTree);
    // Reset so the slot can be used again
    this.functionParamMapping.length = this.functionParamMapping.length - 1;
};


/**
 * Pop the top operator off the operators stack and process the operands
 * it needs then push the collection as a parse tree onto the operators stack.
 */
perseus.Parser.prototype.popOperatorStack = function () {
    var parseTree, operator = this.operators.pop();
    if (operator.unary == true) {
        // Unary operator
        parseTree = new perseus.symbol.UnaryOperation();
        parseTree.push(operator, this.operands.pop());
    } else if (operator.binary == true){
        // Binary operator
        parseTree = new perseus.symbol.BinaryOperation();
        parseTree.push(operator, this.operands.pop(), this.operands.pop());
    //} else if (operator.valueOf() == "(") {
    //} else if (operator.valueOf() == ")") {
        

    } else {
        // TODO other operator ?
        throw "Unhandled operator";
    }

    this.operands.push(parseTree);
};

/**
 * Pops the top set of array values off the array declaration array and adds a 
 * array declaration parse tree to the operands stack.
 */
perseus.Parser.prototype.popArrayDeclarationStack = function () {
    // Add our function call
    var parseTree = new perseus.symbol.ArrayDeclaration();
    //parseTree.push(this.operands.pop());
    while (goog.array.peek(this.arrayDeclarationParamMapping).length > 0) {
        parseTree.push(goog.array.peek(this.arrayDeclarationParamMapping).pop());
    };
    this.operands.push(parseTree);
    // Reset so the slot can be used again
    this.arrayDeclarationParamMapping.length = this.arrayDeclarationParamMapping.length - 1;
};



/**
 * Setting this allows you to ommit the object from object.property's
 * in expressions.
 * @param {string} objectName The object name.
 */
perseus.Parser.prototype.setDefaultObjectName = function (objectName) {
    if (objectName == this.objectName)
        return;

    this.objectName = objectName;

    // We've changed the object name so invalidate the current value as now
    // it may be different
    this.value = null;
};


/**
 * @constructor
 * Defines a precedence level.
 * @param {string} token The token e.g. "+" or "-".
 * @param {number} opt_type 1 for unary operator, 2 for binary.
 */
perseus.Parser.PrecedenceLevel = function (token, opt_type) {
    this.token = token;
    this.type = opt_type || null;
};

/**
 * Is unary
 * @returns {boolean}
 */
perseus.Parser.PrecedenceLevel.prototype.isUnary = function () {
    return (this.type === 1);
};

/**
 * Is unary
 * @returns {boolean}
 */
perseus.Parser.PrecedenceLevel.prototype.isBinary = function () {
    return (this.type === 2);
};


/**
 * Get the next token. Works out what type of token it is.
 */
perseus.Parser.prototype.getNextToken = function () {
    var token = this.lexer.getNextToken();

    if (token == null)
        return null;


    var lexeme = new perseus.Lexeme(token);
    var flags = lexeme.getFlags();

    // If we have no ambiguity we know what token we've got

    if (flags == perseus.Lexeme.Flags.OPERATOR)
        return new perseus.token.Operator(lexeme.valueOf());

    if (flags == perseus.Lexeme.Flags.PUNCTUATOR)
        return new perseus.token.Punctuator(lexeme.valueOf());

    if (flags == perseus.Lexeme.Flags.KEYWORD)
        return new perseus.token.Keyword(lexeme.valueOf());

    if (flags == perseus.Lexeme.Flags.IDENTIFIER) {
        var token = lexeme.valueOf();
        // If we have an identifier we need to ensure it isn't followed by a
        // .property else our identifier should become OBJ.prop
        // We know the object will be all uppercase
        // Commenting out the test for now as lots of unit tests do not
        // follow this requirement.
        //if (token.valueOf().toUpperCase() != token.valueOf())
        //    return token;

        var secondToken = this.lexer.peekToken(1),
            thirdToken = this.lexer.peekToken(2),
            fourthToken = this.lexer.peekToken(3);

        if (secondToken == "." 
            && fourthToken != "("
            && perseus.Lexeme.isIdentifier(thirdToken)) {
            token = token
                + this.lexer.getNextToken()
                + this.lexer.getNextToken();
        }
        return new perseus.token.Identifier(token);
    }

    if (flags == perseus.Lexeme.Flags.LITERAL) {
        var token = lexeme.valueOf(); 
        // If the next token is a . we may have a decimal value.
        if (this.lexer.peekToken(1) == ".") {
            if (goog.string.isNumeric(token)) {
                // Assume decimal
                token = token + this.lexer.getNextToken();
                // If followed by another numeric literal, assume it's the
                // decimal digits
                if (goog.string.isNumeric(this.lexer.peekToken(1))){
                    token = token + this.lexer.getNextToken();
                }
                return new perseus.token.Literal(token);
            }
        }

        return new perseus.token.Literal(token);
    }

    




    // Anything that's filtered through is ambiguous, so solve those cases.

    if (flags & (perseus.Lexeme.Flags.OPERATOR | perseus.Lexeme.Flags.PUNCTUATOR)) {
        // Token is both a punctuator and an operator
        if (lexeme == "(") {
            if (this.previousToken instanceof perseus.token.Identifier) {
                var token = new perseus.token.Operator("(");
            } else {
                // Assume punctuator if not operator
                var token = new perseus.token.Punctuator("(");
            }
            this.bracesStack.push(token);
            return token;
        } else if (lexeme == ")") {
            var token;
            if (goog.array.peek(this.bracesStack) instanceof perseus.token.Operator) {
                token = new perseus.token.Operator(lexeme.valueOf());
            } else {
                // Assume punctuator if not operator
                token = new perseus.token.Punctuator(lexeme.valueOf());
            }
            this.bracesStack.pop();
            return token;
        } else if (lexeme == "[" || lexeme == "]") {
            // TODO Needs work to check that nested expressions work
            // e.g. myArray[(1 + 2)]
            // This is a quick fix to make myArray[0] work
            if ((this.previousToken instanceof perseus.token.Identifier
                    && !(goog.array.peek(this.operators) != null 
                    && goog.array.peek(this.operators).valueOf() == "["
                    && goog.array.peek(this.operators) instanceof perseus.token.Punctuator))
                || (goog.array.peek(this.operators) != null 
                    && goog.array.peek(this.operators).valueOf() == "["
                    && goog.array.peek(this.operators) instanceof perseus.token.Operator))
            {
                return new perseus.token.Operator(lexeme.valueOf());
            }
            // Treat as a punctuator for now
            return new perseus.token.Punctuator(lexeme.valueOf());
        }
    } else {
        throw "Unexpected token sequence";
    }

};


