/**
 * Puts the middle of the array and outwards to the start of the array.
 * @param {Array} arr
 */
titan.array.shuffleMidToStart = function (arr) {
    var half = Math.floor(arr.length / 2) - 1;
    for (var i = half; i < (half * 2) + 2; i++) {
        var toMove = arr.splice(i, 2);
        arr.splice(((i - half) * 2), 0, toMove[1], toMove[0]);
    }

    // Remove any undefineds at the end, occurs if the array had an odd amount 
    // of values
    if (arr[arr.length - 2] == undefined) {
        arr.splice(arr.length - 2, 1);
    }
};


/**
 * Iterate through a range of array records
 * @param {Array} arr
 * @param {goog.math.Range} range The range end value represents the upper limit
 * and the corresponding array index will not be called.
 * @param {Function} func The function to call, passes back the parameters 
 * (object, index). Object is the current array value in question.
 * @param {Object} opt_context The context the callback function will be in.
 */
titan.array.forRange = function (arr, range, func, opt_context) {
    opt_context = opt_context || this;
    for (var i = range.start; i < range.end && i < arr.length; i++) {
        func.call(opt_context, arr[i], i);
    }
};



/**
 * Reverse an array
 * @param {Array} arr
 */
titan.array.reverse = function (arr) {
    var i = 0;
    
    while (i < Math.floor(arr.length / 2)){
        var temp = arr[i];
        arr[i] = arr[arr.length - 1 - i];
        arr[arr.length - 1 - i] = temp;
        i++;
    }

};

/**
 * Calls a function for a range of elements in an array and returns the result
 * into a new array.
 * @param {Array} arr
 * @param {goog.math.Range} range The range end value represents the upper limit
 * and the corresponding array index will not be called.
 * @param {Function} func The function to call, passes back the parameters 
 * (object, index). Object is the current array value in question.
 * @param {Object} opt_context The context the callback function will be in.
 */
titan.array.mapRange = function (arr, range, func, opt_context) {
    var result = [];
    opt_context = opt_context || this;
    for (var i = range.start; i < range.end && i < arr.length; i++) {
        result.push(func.call(opt_context, arr[i], i));
    }
    return result;
};


/**
 * Calls a function for a range of elements in an array and returns the result
 * into a new array.
 * @param {Array} arr
 * @param {goog.math.Range} range The range end value represents the upper limit
 * and the corresponding array index will not be called.
 * @param {Function} func The function to call, passes back the parameters 
 * (object, index). Object is the current array value in question.
 * @param {Object} opt_context The context the callback function will be in.
 */
titan.array.filterRange = function (arr, range, func, opt_context) {
    var result = [];
    opt_context = opt_context || this;
    for (var i = range.start; i < range.end && i < arr.length; i++) {
        if (func.call(opt_context, arr[i], i)) {
            result.push(arr[i]);
        }
    }
    return result;
};


