/**
 * @constructor
 * @param {pb.compile.TokenHandlerInterface} tokenHandler
 * @param {Object} parserFunctions An object with keys corresponding to 
 * operators. Each value for each operator key will be a function that processes
 * operands as the arguments for that operator key.
 */
pb.compile.Evaluator = function (tokenHandler, parserFunctions) {
    this.tokenHandler = tokenHandler;
    this.parserFunctions = parserFunctions;
};

/**
 * Evaluate tokens
 * @param {Mixed} tokens The tokens to evaluate, expects reverse polish 
 * notation.
 */
pb.compile.Evaluator.prototype.evaluatePostfixTokens = function (tokens) {

    var i = 0;
    while (tokens.length > 1) {
        var operator = tokens[i];

        if (this.tokenHandler.isOperator(operator)) {
            
            // Number of operands the parser function takes
            var parserFunction = this.parserFunctions[operator];
            var operandsCount = parserFunction.length;

            // get the operands
            if (i < parserFunction.length)
                throw "Operator " + operator + " expects more arguments.";
            var operands = tokens.splice(i - operandsCount, operandsCount);
            i -= operandsCount;

            // Check that the operands are indeed operands, else we have some
            // invalid tokens
            goog.array.forEach(operands, function (operand) {
                goog.asserts.assert(! this.tokenHandler.isOperator(operand),
                    "Operator " + operator + " expects more arguments."
                );
            }, this);

            // Process the operands by the operator function
            tokens[i] = parserFunction.apply(this.parserFunctions, operands);
        }
        i++;
    }

    return tokens[0];

};

/**
 * Evaluate prefix tokens
 */
pb.compile.Evaluator.prototype.evaluatePrefixTokens = function (tokens) {
    return this.evaluatePostfixTokens(titan.array.reverse(tokens));
};

/**
 * Evaluate an expression string.
 */
pb.compile.Evaluator.prototype.evaluate = function (expressionString) {
    return this.evaluatePostfixTokens(
        this.tokenHandler.getPostfixTokensFromExpression(expressionString)
    );
};
