goog.require('pb.compile.Tokens');
goog.require('goog.array');

/**
 * @constructor
 */
titan.math.AccurateCalculation = function () {
    this.tokens = [];
    for (var i = 0; i < arguments.length; i++) {
        if (typeof arguments[i] == "string") {
            var tokensPart = new pb.compile.Tokens(arguments[i]);
            goog.array.forEach(tokensPart, function (token) {
                this.tokens.push(token);
            }, this);
        } else {
            this.tokens.push(arguments[i]);
        }
    }
    // Convert tokens to postfix
    this.tokens = pb.compiling.convertInfixTokensToPostfix(this.tokens);
    this.processTokens();
};

/***/
titan.math.AccurateCalculation.prototype.processTokens = function () {

    for (var i = 0; i < this.tokens.length; i++) {
        var token = this.tokens[i];
        // Loop through until we find an operator
        if (!pb.compiling.isOperator(token))
            continue;

        var value1 = parseFloat(this.tokens[i - 2]);
        var value2 = parseFloat(this.tokens[i - 1]);
        if (isNaN(value1) == null || isNaN(value2) == null) {
            throw "Non numeric value in calculation";
        }
        var combinedValue;

        var factor1 = 1, factor2 = 1;
        if (token == "*" || token == "/") {
            var limit = 20;
            while (parseInt(value1 * factor1) != (value1 * factor1)) {
                factor1 *= 10;
                limit--;
                if (limit <= 0) {
                    throw "Maximum precision exceeded in calculation.";
                }
            };
            limit = 20;
            while (parseInt(value2 * factor2) != (value2 * factor2)) {
                factor2 *= 10;
                limit--;
                if (limit <= 0) {
                    throw "Maximum precision exceeded in calculation.";
                }
            };
        }

        
        switch (token) {
            case "+": combinedValue = value1 + value2; break;
            case "-": combinedValue = value1 - value2; break;
            case "%": combinedValue = value1 % value2; break;
            case "*": combinedValue = ((value1 * factor1) * (value2 * factor2)) / (factor1 * factor2); break;
            case "/": combinedValue = ((value1 * factor1) / (value2 * factor2)) / (factor1 * factor2); break;
            default: throw "Operator " + token + " not supported";
        }
        
        
        this.tokens.splice(i - 2, 3, combinedValue);
        i -= 2;
    }
    this.value = this.tokens[0];
};

/***/
titan.math.AccurateCalculation.prototype.valueOf = function () {
    return this.value;
};/// <reference path="../../_intellisense.js" />

/**
 * @fileoverview Converts an array of tokens which are values and operators to postfix notation.
 * @deprecated Use the pb.compile.Evaluator
 */

goog.provide('pb.compiling');
goog.require('goog.array');
goog.require('titan.debug.errorDisplay');
// Needs minor modification to remove this dependency
goog.require('pb.Field');
goog.require('titan.math.AccurateCalculation');
goog.require('goog.i18n.DateTimeParse');
goog.require('titan.date.Difference');
