/**
 * Convert infix tokens to postfix tokens
 * @param {Array<mixed>} tokens An array of tokens in infix form.
 * @returns {Array<mixed>} An array of the tokens re-organised into postfix form.
 */
pb.compiling.convertInfixTokensToPostfix = function (tokens) {

    var stack = [], output = [];


    goog.array.forEach(tokens, function (token) {

        if (token == "#") {
            // Push straight onto the output as this indicates a sub query, we also want to push it on the end to indicate when the sub query closes
            output.push(token);
        }
        if (pb.compiling.isOperator(token)) {
            // Is an operator

            while (stack.length > 0
                // There is an operator token at the top of the stack
                && pb.compiling.isOperator(stack[stack.length - 1])
                // The token has less than or equal precendence to the top of the stack token
                && pb.compiling.higherOrEqualPrecedence(stack[stack.length - 1], token)) {
                var t = stack.pop();
                output.push(t);
            };
            stack.push(token);

        } else if (token == "("){
            // Is a left brace
            stack.push(token);
        } else if (token == ")") {
            while (stack.length > 0
                // Token at the top of the stack is not left parenthesis
                && stack[stack.length - 1] != "(") {
                var t = stack.pop();
                output.push(t);
            }
            // Pop the left parenthesis
            if (stack[stack.length - 1] != "(") {
                var error = new Error("Miss matched parenthesis in expression");
                error.display();
                throw error;
            }
            stack.pop();
            
        } else {
            // Is not an operator or parenthesis, is a value
            output.push(token);
        }
    });

    while (stack.length > 0) {
        var t = stack.pop();
        if (t == "(" || t == ")") {
            var error = new Error("Miss matched parenthesis in expression");
            error.display();
            throw error;
        }
        output.push(t);
    }
    return output;
}

/**
 * Does operator1 have higher precendence than operator 2.
 * @returns {boolean}
 */
pb.compiling.higherOrEqualPrecedence = function (operator1, operator2) {

    if (operator1 == "#")
        return false;

    if ((operator1 == "+" || operator1 == "-" || operator1 == ".") && (operator2 == "*" || operator2 == "/"))
        return false;

    if (operator1 == "&&"
            || operator1 == "||")
        return false;

    if (operator1 == "=="
        || operator1 == "!="
        || operator1 == "<="
        || operator1 == ">="
        || operator1 == "<"
        || operator1 == ">") {
        if (operator2 == "&&"
            || operator2 == "||")
            return true;
        return false;
    }

    if (operator1 == "!")
        return false;

    return true;
}

/**
 * Check if a token is an operator
 * @returns {boolean}
 */
pb.compiling.isOperator = function (token) {
    if (typeof token != "string")
        return false;
    if (pb.compiling.OPERATORS[token.toLowerCase()] != null)
        return true;
    return false;
};

/**
 * Valid operators and synonyms. All lower case.
 * @enum
 */
pb.compiling.OPERATORS = {
    "+": "+",
    ".": ".",
    "-": "-",
    "*": "*",
    "/": "/",
    "%": "%",
    "==": "==",
    "=": "==",
    "!=": "!=",
    "<>": "!=",
    "<=": "<=",
    ">=": ">=",
    "<": "<",
    ">": ">",
    "#TOTAL" : "#TOTAL",
    "#AVERAGE" : "#AVERAGE",
    "#MIN" : "#MIN",
    "#MAX": "#MAX",
    "#UNIQUE": "#UNIQUE",
    "#": "#",
    "?": "?",
    ":": ":",
    "&&": "&&",
    "and": "&&",
    "||": "||",
    "or": "||",
    "in": "in",
    "contains": "contains",
    "matches": "matches",
    "toupper": "toupper",
    "tolower": "tolower",
    "totitle": "totitle",
    "isunique": "isunique",
    "daydiff": "daydiff",
    "monthdiff": "monthdiff",
    "yeardiff": "yeardiff",
    "adddays": "adddays",
    "addmonths": "addmonths",
    "addyears": "addyears",
    "!": "!",
    "not": "!",
    "caption": "caption",
    "getchildcontrolrowcount": "getchildcontrolrowcount"
}


/**
 * After evaluating tokens you can get the fields the expression makes use of
 */
pb.compiling.getFieldsUsed = function (tokens) {
    var fields = [];
    var fieldUsed = function (field) {
        fields.push(field);
    };

    pb.compiling.evaluatePostFixTokens(tokens, null, fieldUsed);

    return fields;
};

/**
 * Evaluates post fix tokens to return a value
 * @param {Array} tokens The tokens
 * @param {Function} opt_fieldGetter A custom method to parse OBJ.Property strings to return a value.
 * @param {Function} opt_returnFieldsInUseCallback Returns a field that's used by the expression.
 */
pb.compiling.evaluatePostFixTokens = function (tokens, opt_fieldGetter, opt_returnFieldsInUseCallback) {

    var checkAndConvertFieldToValue = function (token) {
        if (typeof token == "string"
        && /([a-zA-Z]{1}[a-zA-Z0-9_]*\.[a-zA-Z0-9_]+)/ig.test(token)) {

            if (opt_fieldGetter != null) {
                token = opt_fieldGetter(token);
            } else {
                token = Field.getInstance(token);
            }
            if (/*token instanceof pb.Field || */token instanceof pb.fields.AbstractBase) {
                if (opt_returnFieldsInUseCallback != null)
                    opt_returnFieldsInUseCallback.call(this, token);
            }
        }
        return token;
    };

    var removeElseTernaryValue = false;
    var ternaryConditionOpen = false;
    var limit = 500;
    for (var i = 0; i < tokens.length; i++) {
        limit--;
        if (limit < 0) {
            var error = new Error("Maximum limit reached for evaluating expression");
            error.display();
            throw error;
        };
        var token = tokens[i];

        // Look ahead for a IsUnique rule
        /*if (tokens[i + 2] == "isunique") {
            // Re-adjust tokens so they are in the normal format, as isunique is only an alias
            var objPropStr = tokens[i + 1];
            var parts = objPropStr.split(".");
            if (parts.length != 2) {
                var error = new Error("Field not provided for IsUnique rule");
                error.display();
                throw error;
            }
            tokens.splice(i + 1, 2, parts[0], "#", "UNIQUE", true, objPropStr, "?", "#");
            i = i + 2;
            token = tokens[i];
        }*/
        // Look ahead for a IsUnique rule
        // Modified IsUnique
        if (tokens[i] == "isunique") {
            var root = new XMLDataSetReader(window["XMLDataSet"]);
            var uc = root.findDeepestUCNode();
            // Make sure we are working on the current level
            if (uc != null) {
                root = uc.getParentUCNode();
            }
            var field = tokens[i - 1];
            var value = field.getValue();
            var childScreenQueryTokens = [
                /*(field instanceof pb.Field) 
                    ? field.strObject + "." + field.strProperty
                    // Assume it's the new type of field
                    : */field.getObjectName() + "." + field.getPropertyName(),
                value, "==", 1, "?"];
            var xmlUnique = pb.compiling.evaluateChildScreenPostFixTokens(root, tokens[i - 1].strObject, childScreenQueryTokens, undefined, "UNIQUE");
            var value = pb.compiling.evaluateChildScreenPostFixTokens(root, tokens[i - 1].strObject, childScreenQueryTokens, undefined, "TOTAL");
            var isUnique = (value > 0 && xmlUnique) ? false : true;
            tokens.splice(i - 1, 2, isUnique);
            i = i - 1;
            continue;

        }
        
        if (tokens[(i + 1)] == "getchildcontrolrowcount") {

            var parts = tokens[i].split(".");
            var childObject = parts[0];
            var childSubObject = parts[1];

            if (window["XMLDataSet"] == null) {
                throw "The expression function 'GetChildControlRowCount' requires the XMLDataSet to exist";
            }

            //var parts = childObject.split(".");

            var parser = new XMLDataSetReader(window["XMLDataSet"]);
            var uc = parser.findDeepestUCNode() || parser;
            if (uc.node.nodeName != childObject) {
                uc = uc.getObjects(childObject);
                uc = uc[0];
                if (uc.length > 1) {
                    // We have an ambigous child object contender.
                    throw "Ambiguous child object lookup";
                } else if (uc.length == 0) {//var error = new Error("Invalid parameter passed for GetChildControlRowCount()");
                    //throw error;
                    tokens.splice(i, 2, 0);
                    continue;
                    break;
                }
            }
            

            var childObjects = uc.getObjects(childSubObject);
            tokens.splice(i, 2, childObjects.length);
            continue;
        }




        token = checkAndConvertFieldToValue.call(this, token);
        tokens[i] = token;
        
        if (typeof token == "string" && token.slice(0, 1) == "#") {
            var type = token.slice(1);
            if (type == "") {
                switch (tokens[i + 1]) {
                    case "MIN": tokens.splice(i + 1, 1); type = "MIN"; break;
                    case "MAX": tokens.splice(i + 1, 1); type = "MAX"; break;
                    case "AVERAGE": tokens.splice(i + 1, 1); type = "AVERAGE"; break;
                    case "TOTAL": tokens.splice(i + 1, 1); type = "TOTAL"; break;
                    case "UNIQUE": tokens.splice(i + 1, 1); type = "UNIQUE"; break;
                    default: type = "TOTAL"; break;
                }
            }
            type = (type == "") ? "TOTAL" : type;
            // Child screen query
            var screenObject = tokens[i - 2];
            var childObjectStr = tokens[i - 1];
            // TODO this can not come from the root if it is a child > child screen.
            var root = new XMLDataSetReader(window["XMLDataSet"]);
            var uc = root.findDeepestUCNode() || root;
            // Make sure we are working on the current level
            // Use the first uc node if we can if it is the correct node. Else look for the first child
            // First child is not the accurate why to do things but it provides some backwards compatiblity for now.
            var childScreenObject = (screenObject == uc.node.nodeName) ? uc : uc.getObjects(screenObject)[0];
            if (childScreenObject == null) {
                //childScreenObject = uc;
            }
            
            if (childScreenObject == null) {
                //var error = new Error("Child screen " + screenObject + " not found");
                //error.display();
                //throw error;

                // Child screen object won't exist if it has no child objects

            }
            var childScreenQueryTokens = [];
            for (var j = i + 1; i < tokens.length; j++) {
                if (tokens[j] == "#")
                    break;
                childScreenQueryTokens.push(tokens[j]);
            }
            var value;
            if (childScreenObject == null) {
                value = 0;
            } else {
                value = pb.compiling.evaluateChildScreenPostFixTokens(childScreenObject, childObjectStr, childScreenQueryTokens, undefined, type);
            }
            tokens.splice(i - 2, childScreenQueryTokens.length + 4, value);
            i = i - 2;
            continue;
        }




        if (token == "toupper") {
            tokens[i - 1] = tokens[i - 1].toUpperCase();
            tokens.splice(i, 1);
            continue;
        } else if (token == "tolower") {
            tokens[i - 1] = tokens[i - 1].toLowerCase();
            tokens.splice(i, 1);
            continue;
        } else if (token == "totitle") {
            var parts = tokens[i - 1].split(" ");
            goog.array.forEach(parts, function (part, index) {
                if (part.length > 0) {
                    parts[index] = part.slice(0, 1).toUpperCase() + part.slice(1);
                }
            }, this);
            tokens[i - 1] = parts.join(" ");
            tokens.splice(i, 1);
            continue;
        } else if (token == "daydiff" || token == "monthdiff" || token == "yeardiff") {
            var parser = new goog.i18n.DateTimeParse("dd/MM/yyyy");

            var date1;
            if (tokens[i - 2] instanceof Date) {
                date1 = tokens[i - 2];
            } else if (/*tokens[i - 2] instanceof pb.Field || */tokens[i - 2] instanceof pb.fields.AbstractBase) {
                date1 = tokens[i - 2].getValue();
            } else {
                date1 = new Date();
                if (!(typeof tokens[i - 2] == "string" && tokens[i - 2].toLowerCase() == "today")) {
                    // Leave the date as is if it is 'TODAY'
                    parser.parse(tokens[i - 2], date1);
                }
            }
            
            var date2;
            if (tokens[i - 1] instanceof Date) {
                date2 = tokens[i - 1];
            } else if (/*tokens[i - 1] instanceof pb.Field || */tokens[i - 1] instanceof pb.fields.AbstractBase) {
                date2 = tokens[i - 1].getValue();
            } else {
                date2 = new Date();
                if (!(typeof tokens[i - 1] == "string" && tokens[i - 1].toLowerCase() == "today")) {
                    // Leave the date as is if it is 'TODAY'
                    parser.parse(tokens[i - 1], date2);
                }
            }

            var value;
            var diff = new titan.date.Difference(date1, date2);
            switch (token) {
                case "daydiff": value = diff.getDays(); break;
                case "monthdiff": value = diff.getMonths(); break;
                case "yeardiff": value = diff.getYears(); break;
            }
            tokens.splice(i - 2, 3, value);
            i-=2;
            continue;
        } else if (token == "adddays" || token == "addmonths" || token == "addyears") {
            

            var date1;
            if (tokens[i - 2] instanceof Date) {
                date1 = tokens[i - 2];
            } else if (/*tokens[i - 2] instanceof pb.Field || */tokens[i - 2] instanceof pb.fields.AbstractBase) {
                date1 = tokens[i - 2].getValue();
            } else {
                date1 = new Date();
                var parser = new goog.i18n.DateTimeParse("dd/MM/yyyy");
                parser.parse(tokens[i - 2], date1);
            }
            
            var amount = tokens[i - 1];
            var interval; 
            switch(token){
                case "adddays": interval = new goog.date.Interval(0, 0, amount); break;
                case "addmonths": interval = new goog.date.Interval(0, amount); break;
                case "addyears": interval = new goog.date.Interval(amount); break;
            }
            
            date1 = new goog.date.Date(date1.getFullYear(), date1.getMonth(), date1.getDate());
            date1.add(interval);
            tokens.splice(i - 2, 3, date1.date_);
            i-=2;
            continue;
        }




        // Loop through until we find an operator
        if (!pb.compiling.isOperator(token))
            continue;

        

        // TODO: Try to convert and obj.prop string to an instance of a field for the below operations
        tokens[(i - 2)] = checkAndConvertFieldToValue.call(this, tokens[(i - 2)]);
        tokens[(i - 1)] = checkAndConvertFieldToValue.call(this, tokens[(i - 1)]);

        // If we want the caption of a list box instead of the value process it here
        if (typeof token == "string" && token.toLowerCase() == "caption") {
            if (/*tokens[i - 1] instanceof pb.Field || */tokens[i - 1] instanceof pb.fields.AbstractBase) {
                tokens[i - 1] = tokens[i - 1].getCaption();
                tokens.splice(i, 1);
                continue;
            }
        } 
        
        // If the token is a field then convert it to a value
        if (/*token instanceof pb.Field || */token instanceof pb.fields.AbstractBase) {
            token = token.getValue();
            tokens[i] = token;
        }

        // in is a special case as it doesn't process just 2 values it processes potentially infinite values
        // in should only occur in boolean expressions so if it finds itself out of place treat it as a string
        if (token == "in") {
            // Can be a value in a list of values
            // Or a value in a regex, equates to if regex matches value
            if (tokens[i - 1] instanceof RegExp) {
                // Treat it as a matches
                token = "matches";
            } else {
                // Handle list of values
                var valuesPool = [];
                for (var j = i - 1; j >= 0 && (tokens[j] !== false && tokens[j] !== true) ; j--) {
                    valuesPool.push(tokens[j]);
                }
                // Pop the end value off as this is the value we are checking is in the values Pool
                var checkValue = valuesPool.pop();
                var match = goog.array.find(valuesPool, function (value) {
                    if (checkValue == value)
                        return true;
                });
                var finalValue = (match !== null) ? true : false;
                tokens.splice(i - (valuesPool.length + 1), (valuesPool.length + 2), finalValue);
                continue;
            }
        } 

        // If token is a not operator is simply inverses the last value
        if (token == "!") {
            if (/*tokens[i - 1] instanceof pb.Field || */tokens[i - 1] instanceof pb.fields.AbstractBase) {
                tokens[i - 1] = tokens[i - 1].getValue();
                if (!(tokens[i - 1] === false || tokens[i - 1] === true)) {
                    var error = new Error("Not operator applied to field that is not a checkbox");
                    error.display();
                    throw error;
                }
            };
            tokens[i - 1] = !tokens[i - 1];
            tokens.splice(i, 1);
            continue;
        }

        if (/*tokens[(i - 2)] instanceof pb.Field || */tokens[i - 2] instanceof pb.fields.AbstractBase) {
            tokens[(i - 2)] = tokens[(i - 2)].getValue();
        }

        if (/*tokens[(i - 1)] instanceof pb.Field || */tokens[i - 1] instanceof pb.fields.AbstractBase) {
            tokens[(i - 1)] = tokens[(i - 1)].getValue();
        }

        // All these token operators process last two tokens
        var finalValue,
            value1 = tokens[(i - 2)],
            value2 = tokens[(i - 1)],
            operator = token;

        if (operator == "?") {
            ternaryConditionOpen = true;
            if (value1 == true) {
                tokens.splice(i - 2, 3, value2);
                removeElseTernaryValue = true;
                ternaryConditionOpen = false;
            } else {
                tokens.splice(i - 2, 3);
                // Value will be replaced with the correct value if there is an else condition on next pass through.
            }
            i = i - 3;
            continue;
        }
        if (operator == ":") {
            if (removeElseTernaryValue) {
                removeElseTernaryValue = false;
                tokens.splice(i - 1, 2);
                i = i - 2;
            } else {
                tokens.splice(i, 1);
                i = i - 1;
            }
            continue;
        }
        

        switch (operator) {
            // Concat values as strings
            case ".": finalValue = "" + value1 + value2; break;
            // Attempt to treat numeric strings as numbers
            case "+": finalValue = value1 + value2; break;
            case "-":
                if (value1 instanceof Date && value2 instanceof Date) {
                    finalValue = ((value1 - (value1 % 86400000)) - (value2 - (value2 % 86400000))) / 86400000;
                } else {
                    finalValue = value1 - value2; 
                }
                break;
            case "*": finalValue = (typeof value1 == "number" && typeof value2 == "number") ? (new titan.math.AccurateCalculation(value1, " * ", value2)).value : value1 * value2; break;
            case "/": finalValue = (typeof value1 == "number" && typeof value2 == "number") ? (new titan.math.AccurateCalculation(value1, " / ", value2)).value : value1 / value2; break;
            case "%": finalValue = value1 % value2; break;
            case "==": finalValue = value1 == value2; break;
            case "!=": finalValue = value1 != value2; break;
            case "<=": finalValue = value1 <= value2; break;
            case ">=": finalValue = value1 >= value2; break;
            case "<": finalValue = value1 < value2; break;
            case ">": finalValue = value1 > value2; break;
            case "&&": finalValue = (value1 == true && value2 == true) ? true : false; break;
            case "||": finalValue = (value1 == true || value2 == true) ? true : false; break;
            case "matches": finalValue = value2.test(value1); break;
            case "contains": finalValue = (goog.array.indexOf(value1.toLowerCase(), value2.toLowerCase()) == false) ? true : false;
        }

        tokens.splice((i - 2), 3, finalValue);
        i = (i - 2);
    }



    // Ternary condition but the else parameter wasn't supplied
    // So return a 0
    if (removeElseTernaryValue && tokens.length == 0)
        return 0;

    if (/*tokens[0] instanceof pb.Field || */tokens[0] instanceof pb.fields.AbstractBase) {
        return tokens[0].getValue();
    }
    return tokens[0];
}



/**
 * Evaluate series of tokens for a child screen sum.
 * @param {pb.FieldObjectNode} screenObject
 * @param {Array} tokens
 */
pb.compiling.evaluateChildScreenPostFixTokens = function (screenObject, childObjectStr, tokens, opt_returnFieldsInUseCallback, type) {
    // Check that all the objects in this container are the same
    var objects = screenObject.getObjects(childObjectStr);
    var currentObjStr = null;
    var invalid = goog.array.every(objects, function (obj) {
        if (currentObjStr == null) {
            currentObjStr = obj.getNodeName();
        }
        if (currentObjStr != obj.getNodeName())
            return true;
    }, this);

    if (invalid && objects.length > 0) {
        var error = new Error("Child screen query on " + screenObject.getNodeName() + " has more than 1 object.");
        error.display();
        throw error;
    }

    var total = 0;
    var count = 0;
    var min = null;
    var max = null;
    var average = null;
    var values = [];
    var isUnique = true;
    goog.array.forEach(objects, function (obj) {
        if (obj.getPropertyValue("UC") == "1")
            return;
        var fieldGetter = function (fieldStr) {
            var temp = fieldStr.split(".");
            // The object should/will always be the same
            var tempObject = temp[0];
            var tempProperty = temp[1];


            var value = obj.getPropertyValue(tempProperty);
            
            var t = type.toUpperCase();
            if (t == "TOTAL"
                || t == "MAX"
                || t == "MIN"
                || t == "AVERAGE") {
                // Case value to a number
                // As we don't have any info about what type the property is we will try to convert to
                // a float if we can for most cases.
                value = value || 0;
                var temp = window["parseFloat"](value);
                if (window["isNaN"](temp)) {
                    /* There is no info in the XML about what data type we are working with, so to fit the best case match we will
                     * try to convert to a number if we can
                    var error = new Error("A child screen query using a numeric operator encountered a non numeric field.");
                    error.display();
                    throw error;*/
                    return value;
                }
                return temp;
            }
            return value;
        };
        var tokensClone = goog.array.clone(tokens);
        var val = pb.compiling.evaluatePostFixTokens(tokensClone, fieldGetter, opt_returnFieldsInUseCallback);
        var t = type.toLowerCase();
        
        if (val != undefined && goog.array.indexOf(values, val) != -1) {
            isUnique = false;
        }
        values.push(val);
        if (val != null || val != undefined) {
            total += val;
            if (min == null || min > val) {
                min = val;
            }
            if (max == null || max < val) {
                max = val;
            }
            count++;
        }
        
    }, this);
    average = total / count || 0;

    switch (type) {
        case "MIN": return min || 0;
        case "MAX": return max || 0;
        case "AVERAGE": return average || 0;
        case "UNIQUE": return isUnique;
        case "TOTAL":
        default:
            return total || 0;
    }

};

