pb.CheckboxGrouping.prototype.handleCheckboxChange = function(e){
    var checkbox = e.target;
	

    // Temp fix to prevent errors
    // TODO update how checkboxes work now with new knowledge of onload event
    if (checkbox == null)
        return;
    
		
    var group = this.getGroupForElement(checkbox);
    if (group.getMode() == pb.CheckboxGroupMode.All) {
        // One checked is all checked
        for(var i = 0; i < group.getCount(); i++){
            group.getElementAt(i).setChecked(checkbox.getChecked());
        }
    } else if (group.getMode() == pb.CheckboxGroupMode.Radio){
        // One must be checked
        if (checkbox.getChecked()) {
            // Uncheck all others
            for (var i = 0; i < group.getCount() ; i++) {
                if (group.getElementAt(i) != checkbox) {
                    // Uses setValueInternal to prevent event loop firing
                    //group.getElementAt(i).setValueInternal(false);
                    group.getElementAt(i).setValue(false);
                }
            }
        } else {
            // Do not allow unchecking, as one checkbox must always be checked
            // Should not hit here...
            // Check if others are unchecked
            var allUnchecked = true;
            for (var i = 0; i < group.getCount() ; i++) {
                if (group.getElementAt(i) != checkbox) {
                    // Uses setValueInternal to prevent event loop firing
                    if (group.getElementAt(i).getChecked())
                        allUnchecked = false;
                }
            }
            if (allUnchecked) {
                e.preventDefault();
                checkbox.setChecked(true);
            }
        }
    
    } else if (group.getMode() == pb.CheckboxGroupMode.Single){
        // Checkbox is checked so uncheck any other checkboxes in the group
        if (!checkbox.getChecked())
            return;
        for(var i = 0; i < group.getCount(); i++){
            if (group.getElementAt(i) != checkbox){
                group.getElementAt(i).setChecked(false);
            }
        }
    } else {
        // ...
    }
};

