/**
 * A collection of checkboxes
 * @implements {goog.structs.Collection}
 */
pb.CheckboxGroup = function () {
    this.internalArray_ = [];
};

/**
 * Add a checkbox to the group.
 */
pb.CheckboxGroup.prototype.add = function (checkbox) {
    if (this.contains(checkbox))
        return;

    this.internalArray_.push(checkbox);
};

/**
 * Check if the group contains a checkbox.
 */
pb.CheckboxGroup.prototype.contains = function (checkbox) {
    return -1 != goog.array.indexOf(this.internalArray_, checkbox);
};


/**
 * Get the count of checkboxes in the group.
 */
pb.CheckboxGroup.prototype.getCount = function () {
    return this.internalArray_.length;
};


/**
 * Remove a checkbox from the group
 */
pb.CheckboxGroup.prototype.remove = function (checkbox) {
    var index = goog.array.indexOf(this.internalArray_, checkbox);
    if (index != -1) {
        this.internalArray_.splice(index, 1);
    }
};

/**
 * Get a checkbox at a specific index position
 * @param {number} index
 */
pb.CheckboxGroup.prototype.getElementAt = function (index) {
    return this.internalArray_[index];
};


/**
 * Set group's mode
 * @param {pb.CheckboxGroupMode} mode
 */
pb.CheckboxGroup.prototype.setMode = function (mode) {
    this.mode_ = mode;
};

/**
 * Get the group's mode
 * @return {pb.CheckboxGroupMode}
 */
pb.CheckboxGroup.prototype.getMode = function () {
    return this.mode_ || pb.CheckboxGroupMode.Default;
}; 

/**
 * Set the checkbox group mode.
 * @enum
 */
pb.CheckboxGroupMode = {

    /**
     * One checkbox checked means all checkboxes are checked.
     */
    All: "all",

    /**
     * One or no checkboxes can be selected.
     */
    Single: "single",

    /**
     * One checkbox must always be selected.
     */
    Radio: "radio",

    /**
     * Any combination of checkboxes can be selected.
     */
    Default: "default"
};

window["Groups"] = pb.CheckboxGrouping;
window["Groups"]["getInstance"] = pb.CheckboxGrouping.getInstance;


