/**
 * @constructor
 * @param {string} objectName
 * @param {string} propertyName
 */
pb.fields.Text = function (objectName, propertyName) {
    goog.base(this, objectName, propertyName);
};
goog.inherits(pb.fields.Text, pb.fields.AbstractBase);

/**
 * Builds the pb.fields.Text's DOM tree and stores its root in this.element_.
 */
pb.fields.Text.prototype.createDom = function () {
    goog.base(this, 'createDom');
    this.element_ = this.dom_.createElement("div");
    this.input_ = this.dom_.createDom("input", {
        "name":    "ctl00$cntMainBody$" + this.objectName.toUpperCase()
            + "__" + this.propertyName,
        "type": "text",
        "class": "form-control"
    });
    this.disabledInput_ = this.dom_.createDom("input", {
        /* Using readonly attribute instead see issue #100 */
        /*disabled: "disabled",*/
        readOnly: "readOnly",
        "class": "hidden form-control"
    });
    this.element_.appendChild(this.input_);
    this.element_.appendChild(this.disabledInput_);
};


/**
 * Takes an Element that is already present in the document, performs any transformations necessary to turn it into a 
 * structure equivalent to the structure created by createDom(), and stores it in this.element_.
 * @param {Element} element
 */
pb.fields.Text.prototype.decorateInternal = function (element) {
    goog.base(this, 'decorateInternal', element);

    this.input_ = goog.dom.query("input", element)[0];

    /*if (this.input_.name == null) {
        this.input_.name = "ctl00$cntMainBody$" + this.objectName.toUpperCase()
            + "__" + this.propertyName;
    }*/

    // Match any classes
    this.disabledInput_ = this.dom_.createDom("input", {
        "readOnly": "readOnly",
        "class": this.input_.className + " hidden"
    });

    goog.dom.insertSiblingAfter(this.disabledInput_, this.input_);

    // If the field is disabled then disable it
    if (this.input_.getAttribute("disabled") == "disabled"
        || (this.input_.getAttribute("readOnly") != null
        //  IE returns false if the attribute doesn't exist
        && this.input_.getAttribute("readOnly") != false)) {
        this.setReadOnly(true);
    }

    if (this.formatter) {
        this.formatter.setBaseValue(this.input_.value);
        this.formatter.decorate(this.input_);
        this.displayInput = this.formatter.getFakeInput();
        this.formatter.setCustomFormatPattern(pattern);
    }
};


