/**
 * Set the field to mandatory.
 * @param {boolean} value
 * @param {String} opt_mandatoryMessage An optional mandatory message to display
 * to the user, has no effect unless 'value' is true.
 */
pb.fields.Text.prototype.setMandatory = function (value, opt_mandatoryMessage) {
    goog.base(this, 'setMandatory', value, opt_mandatoryMessage);
    if (value) {
        goog.dom.classes.add(this.input_, "mandatory", "field-mandatory");
        goog.dom.classes.add(this.disabledInput_, "mandatory", "field-mandatory");
        
    } else {
        goog.dom.classes.remove(this.input_, "mandatory", "field-mandatory");
        goog.dom.classes.remove(this.disabledInput_, "mandatory", "field-mandatory");
    }
    if (this.placeholder
        // This check doesn't work
        /*&& this.placeholder.isInDocument()*/) {
        this.placeholder.onBlur();
    }
};

/**
 * Set the field to readonly or not. Also disables the field
 * @param {boolean} value
 */
pb.fields.Text.prototype.setReadOnly = function (value) {

    // If the input is hidden anyway there's no need to do anything
    if (this.input_.getAttribute("type") == "hidden") return;

    // Do not allow enabling the field in 'View' mode.
    if (this.permanentReadOnly && value == false) return;

    goog.base(this, 'setReadOnly', value);
    this.disabledInput_.value = this.getDisplayedValue();
    if (value) {
        goog.dom.classes.add(this.input_, "hidden");
        goog.dom.classes.remove(this.disabledInput_, "hidden");
        goog.dom.classes.add(this.disabledInput_, "readonly");
    } else {
        goog.dom.classes.remove(this.input_, "hidden");
        goog.dom.classes.add(this.disabledInput_, "hidden");
        goog.dom.classes.remove(this.disabledInput_, "readonly");
    }
};

/**
 * Sets if the element should be visible or not. Non visible elements still take
 * up space in the document they just aren't visible.
 * @param {boolean} value
 */
pb.fields.Text.prototype.setVisible = function (value) {
    this.element_.style.visibility = (value) ? "visible" : "hidden";

    // Do this after so we don't trigger a visibility change event until the 
    // work is done
    goog.base(this, 'setVisible', value);
};


/**
 * Abstracts away what type of form input it is and get's the value.
 * For example, for a checkbox a boolean is returned, for a text box a string is 
 * returned. A date field, a date is returned.
 */
pb.fields.Text.prototype.getValue = function () {
    return this.input_.value;
};



/**
 * Set the value of the element. 
 * Use <span class="highlight">true</span> and <span class="highlight">false</span> for checkboxes. 
 * @param {Mixed} value
 */
pb.fields.Text.prototype.setValue = function (value) {

    // --- Prevent infinite loop code ---
    if (this.constructor == pb.fields.Text && this.busy) return;
    // Do not want to call this if triggered from a sub class
    if (this.constructor == pb.fields.Text) this.busy = true;
    // --- Prevent infinite loop code END ---

    // Do not allow setting a value if in 'View' mode.
    if (this.unchangeable) { this.busy = false; return; }

    if (value == null)
        value = ''

    if (value != this.getValue()){
      this.setValueInternal(value);
      this.dispatchEvent("change");
    }

    // --- Prevent infinite loop code ---
    // Do not want to call this if triggered from a sub class
    if (this.constructor == pb.fields.Text) this.busy = false;
    // --- Prevent infinite loop code END ---
};


/**
 * Set the value without triggering a change event
 */
pb.fields.Text.prototype.setValueInternal = function (value) {

    // Do not allow setting a value if in 'View' mode.
    if (this.unchangeable) { this.busy = false; return; }

    if (value instanceof Promise){
        var self = this;
        value.then(function(value){
            self.setValueInternal(value);
        });
        return;
    }

    if (value instanceof pb.fields.AbstractBase) {
        value = value.getValue();
    }

    this.input_.value = (value == null) ? "" : value;
    if (this.isReadOnly())
        this.disabledInput_.value = (value == null) ? "" : value;

    if (this.placeholder) {
        this.placeholder.update();
    }

};

/**
 * Make a field completely hidden so that it does not take up space in the 
 * document. The inverse of setDisplay().
 */
pb.fields.Text.prototype.setHidden = function (value) {
    
    if (value) {
        goog.dom.classes.add(this.element_, "hidden");
    } else {
        goog.dom.classes.remove(this.element_, "hidden");
    }
    
    // Do this after so we don't trigger a visiblity change until the work
    // is actually done
    goog.base(this, 'setHidden', value);


    
};


/**
 * Gets the value displayed, this may or may not be different to the true value
 * that will be posted.
 */
pb.fields.Text.prototype.getDisplayedValue = function () {
    return this.input_.value;
};

/**
 * Set width
 * @param {Number} pixels
 */
pb.fields.Text.prototype.setWidth = function (pixels) {
    this.disabledInput_.style.width = pixels + "px";
    this.input_.style.width = pixels + "px";

    if (this.placeholder
        // This check doesn't work
        /*&& this.placeholder.isInDocument()*/) {
        this.placeholder.onBlur();
    }
};

/**
 * Get width
 * @return {Number}
 */
pb.fields.Text.prototype.getWidth = function () {
    var bounds;
    if (this.isReadOnly())
        bounds = goog.style.getBounds(this.disabledInput_);
    else
        bounds = goog.style.getBounds(this.input_);

    return bounds.width;
};


/**
 * Set the placeholder value
 */
pb.fields.Text.prototype.setPlaceholder = function (value) {
    this.placeHolderText_ = value;
    if (!this.placeholder) {
        this.placeholder = new titan.ui.Placeholder(value);
        this.placeholder.decorate(this.input_);
    } else {
        this.placeholder.setValue(value);
    }

    if (this.displayInput && !this.displayInputplaceholder) {
        this.displayInputplaceholder = new titan.ui.Placeholder(value);
        this.displayInputplaceholder.decorate(this.displayInput);
    } else if (this.displayInputplaceholder){
        this.displayInputplaceholder.setValue(value);
    }
};



/**
 * Get the input field that has the name attribute attached.
 */
pb.fields.Text.prototype.getInput = function () {
    return this.input_;
};


