/**
 * Make the input text bold
 * @param {boolean} enable
 */
pb.fields.Text.prototype.setBold = function (enable) {
    if (enable) {
        this.input_.style.fontWeight = "bold";
        this.disabledInput_.style.fontWeight = "bold";
    } else {
        this.input_.style.fontWeight = "normal";
        this.disabledInput_.style.fontWeight = "normal";
    }
    if (this.placeholder
        // This check doesn't work
        /*&& this.placeholder.isInDocument()*/) {
        this.placeholder.onBlur();
    }
};


/**
 * Set the text alignment on the field.
 * @param {string} alignment Accepts left, centre, center, middle and right.
 */
pb.fields.Text.prototype.setTextAlign = function (alignment) {
    alignment = alignment.toLowerCase();
    if (alignment == "centre" || alignment == "middle"){
        alignment = "center";
    }

    this.input_.style.textAlign = alignment;
    this.disabledInput_.style.textAlign = alignment;
    if (this.placeholder && this.placeholder.getFakeInput())
        this.placeholder.getFakeInput().style.textAlign = alignment;
    if (this.placeholder
        // This check doesn't work
        /*&& this.placeholder.isInDocument()*/) {
        this.placeholder.onBlur();
    }
    
};

