/**
 * Get code
 * @public
 */
pb.ui.ComboBox.prototype.getCode = function () {
    return this.code;
};




/**
 * Set value from code
 */
pb.ui.ComboBox.prototype.setByCode = function (code) {

    var menu = this.getMenu();
    var items = menu.getItems();
    var menuitem = goog.array.find(items, function (item) {
        if (item.getCode() == code) {
            return true;
        }
    }, this);
    if (menuitem) {
        this.labelInput_.setValue(menuitem.getCaption());
        // Expensive in IE8
        if (!(goog.userAgent.IE && goog.userAgent.isVersion(8)))
            menu.setHighlighted(menuitem)

        var value = menuitem.getValue();
        this.hiddenField.value = value;
        this.hiddenValue = value;
        this.code = code;
    } else {
        // Option not found don't set a value.
    }

    // If not yet fully loaded we have to grab from the original option elements
    if (!this.fullyLoaded_ && this.selectElement) {
        var select = this.selectElement;

        var success = goog.array.some(select.options, function (option, i) {

            if (option.getAttribute("code") == code) {
                this.labelInput_.setValue(option.innerHTML);
                var comboItem = new pb.ui.ComboBoxItem(
                    goog.string.unescapeEntities(option.innerHTML),
                    option.value,
                    option.getAttribute("code")
                );
                this.addItemAt(comboItem, 0);
                if (!(goog.userAgent.IE && goog.userAgent.isVersion(8)))
                    this.menu_.setHighlighted(comboItem)

                this.hiddenField.value = option.value;
                this.hiddenValue = option.value;
                this.code = option.getAttribute("code");
                select.selectedIndex = i;

                return true;
            }

        }, this);

        if (!success) {
            // Option not found don't set a value.
        }
        return;
    }
};



