/** 
 * Lazy load items, so add remaining items in now.
 */
pb.ui.ComboBox.prototype.maybeShowMenu_ = function (showAll) {
    this.maybeBuildListItems_();
    goog.base(this, 'maybeShowMenu_', showAll);
};

/** 
 * Returns true if the combo has any options other than 'please select'.
 * @return {bool}
 */
pb.ui.ComboBox.prototype.hasOptions = function () {

    var menu = this.getMenu();
    var items = menu.getItems();
    if (items.length > 1) return true;

    // If not yet fully loaded we have to grab from the original option elements
    if (!this.fullyLoaded_ && this.selectElement) {
        var select = this.selectElement;
        if (select.options.length > 1)
            return true;
    }

    return false;
};

pb.ui.ComboBox.prototype.clearOptions = function () {

    var menu = this.getMenu();
    var items = menu.getItems();
    goog.array.forEach(items, function (item) {
        // "" Is used not 0 as the value for please select
        if (/*item.getValue() != "0" && */item.getValue() != "") {
            menu.removeItem(item);
        }
    }, this);
    this.setValue("");
    this.fullyLoaded_ = true;
};


/**
 * Handle the menu's select event.
 * @param {goog.events.Event} e The event.
 * @private
 */
pb.ui.ComboBox.prototype.onMenuSelected_ = function (e) {


    var menu = this.menu_.getHighlighted();
    this.code = (menu && menu.getCode) ? menu.getCode() : null;

    //goog.log.info(this.logger_, 'onMenuSelected_()');
    var item = /** @type {!goog.ui.MenuItem} */ (e.target);
    // Stop propagation of the original event and redispatch to allow the menu
    // select to be cancelled at this level. i.e. if a menu item should cause
    // some behavior such as a user prompt instead of assigning the caption as
    // the value.
    if (true) {
        var caption = item.getCaption();
        //goog.log.fine(this.logger_,
        //    'Menu selection: ' + caption + '. Dismissing menu');
        if (this.labelInput_.getValue() != caption) {
            this.labelInput_.setValue(caption);
            //this.dispatchEvent(goog.ui.Component.EventType.CHANGE);
        }



        if (this.hiddenField != null && this.menu_.getHighlightedIndex() >= 0)
            this.hiddenField.value = this.hiddenValues[this.menu_.getHighlightedIndex()];
        this.hiddenValue = this.hiddenValues[this.menu_.indexOfChild(e.target)];


        this.dismiss();

        // This has been altered to dispatch after the change is made, else another
        // rule may alter the actual value before the caption is set.
        this.dispatchEvent(new goog.ui.ItemEvent(
            goog.ui.Component.EventType.ACTION, this, item))
    }
    e.stopPropagation();


};

/** 
 * Gets the total amount of options available. Including the please select
 * option.
 * @private
 * @return {number}
 */
pb.ui.ComboBox.prototype.getTotalOptions_ = function () {

    // If not yet fully loaded we have to grab from the original option elements
    if (!this.fullyLoaded_ && this.selectElement) {
        var select = this.selectElement;
        return select.options.length;
    }

    var menu = this.getMenu();
    var items = menu.getItems();
    return items.length;
};


/** 
 * Returns true if the combo has any options other than 'please select'.
 * @return {bool}
 */
pb.ui.ComboBox.prototype.hasOptions = function () {
    return (this.getTotalOptions_() > 1);
};

/** 
 * Returns true if the combo has one option other than 'please select'.
 * @return {bool}
 */
pb.ui.ComboBox.prototype.hasOneOption = function () {
    return (this.getTotalOptions_() == 2);
};


/**
 * Builds the list items, as they are not loaded initially, for optimisation.
 * They are lazy loaded. This method is indempotent.
 * @private
 */
pb.ui.ComboBox.prototype.maybeBuildListItems_ = function ()
{
    
    // Load the elements if not yet loaded
    if ((!this.fullyLoaded_) && this.selectElement)
    {
        var select = this.selectElement;
        
        var labelValue = this.getLabelInput().getValue();
        
        this.removeAllItems();
        goog.array.forEach(select.options, function (option, i)
        {
            if (i == 0)
            {
                // Set text to default to first record.
                this.defaultText_ = option.innerHTML;
            }
            // selected should be null if not selected
            var selected = this.selectElement.selectedIndex == i;
            
            if (selected)
            {
                this.code = option.getAttribute("code");
            }
            
            var comboItem = new pb.ui.ComboBoxItem(
                goog.string.unescapeEntities(option.innerHTML),
                option.value,
                option.getAttribute("code")
            );
            this.addItemAt(comboItem, i);
            
            if (selected)
            {
                this.labelInput_.setValue(option.innerHTML);
                if (!(goog.userAgent.IE && goog.userAgent.isVersion(8)))
                    this.menu_.setHighlighted(comboItem)
                
                this.hiddenField.value = option.value;
                this.hiddenValue = option.value;
                this.code = option.getAttribute("code");
            }

        }, this);
        
        this.fullyLoaded_ = true;
        this.getLabelInput().setValue(labelValue);
    }
};

