/**
 * @constructor
 * @param {string} objectName
 * @param {string} propertyName
 */
pb.fields.List = function (objectName, propertyName) {
    goog.base(this, objectName, propertyName);
};
goog.inherits(pb.fields.List, pb.fields.AbstractBase);

/**
 * Builds the pb.fields.List's DOM tree and stores its root in this.element_.
 */
pb.fields.List.prototype.createDom = function () {
    var element = this.dom_.createDom("div");
    this.combo_ = new pb.ui.ComboBox();
    this.combo_.render(element);
    //getInput
    this.combo_.getInput().setAttribute("name", "ctl00_cntMainBody_" + 
        this.getObjectName() + "__" + this.getPropertyName());
    this.element_ = element;
    return element;
};


/**
 * Takes an Element that is already present in the document, performs any transformations necessary to turn it into a 
 * structure equivalent to the structure created by createDom(), and stores it in this.element_.
 * @param {Element} element
 */
pb.fields.List.prototype.decorateInternal = function (element) {
    pb.fields.AbstractBase.prototype.decorateInternal.call(this, element);

    var internalElement;
    if (element.tagName == "SELECT"){
        internalElement = element;
    } else if (element.tagName == "INPUT") {
        internalElement = element;
    } else {
        internalElement = goog.array.concat(
            goog.array.map(goog.dom.query("input", element), function(o){return o;}),
            goog.array.map(goog.dom.query("select", element), function (o) { return o; })
        )[0];
    }

    this.combo_ = new pb.ui.ComboBox();
    this.combo_.decorate(internalElement);
    //if (internalElement.tagName.toUpperCase() == "INPUT")
    //{
        this.combo_.getInput().setAttribute("name", internalElement.name);
    //}

    goog.dom.classes.add(this.combo_.getElement(), "pb-field");

    if (internalElement.tagName.toUpperCase() == "SELECT") {
        this.combo_.setValue(
            internalElement.selectedIndex != -1
            ? internalElement.options[internalElement.selectedIndex].value
            : ''
        )
    } else if (internalElement.tagName.toUpperCase() == "INPUT") {
        this.combo_.setValue(internalElement.value)
    } 
    
    if (internalElement.getAttribute("disabled") == "disabled"
    || (internalElement.getAttribute("readOnly") != null
        //  IE returns false if the attribute doesn't exist
    && internalElement.getAttribute("readOnly") != false)) {

        this.setReadOnly(true);
    }
    
};


/**
 * Check if the element can be decorated.
 * Must decorate the parent wrapper div that contains the input
 * @private
 */
pb.fields.List.prototype.canDecorate = function (element) {
    

    // Can decorate a hidden input or an existing select
    if (element.tagName == "SELECT" 
       || (element.tagName == "INPUT" /*&& element.getAttribute("type") == "hidden"*/))
        return true;

    // Or can decorate an element that contains a single input or select
    // If more than one input or select exists in the container the decorate
    // is invalid as it is ambigous
    var elements = goog.array.concat(
        goog.array.map(goog.dom.query("input", element), function (o) { return o; }),
        goog.array.map(goog.dom.query("select", element), function (o) { return o; })
    );
    if (elements.length == 1)
      return true;




    return false;

    // Commenting out to make decorating more lenient and will work with inputs
    // not related to exact product builder names. And with input's not on a
    // form, i.e. no name attribute.
    // Must contain an input
    /*var selects = goog.dom.query("select", element);
    if (selects.length != 1)
        return false;

    var missMatchedName = goog.array.find(selects, function (select) {  
        // If the element has an existing name attribute it must match the object 
        // and property name provided.
        if (select.name && select.name
            != "ctl00_cntMainBody_" + this.objectName.toUpperCase()
            + "__" + this.propertyName)
            return true;
    }, this);

    if (missMatchedName)
        return false;

    return true;
    */
};

/**
 * Set the field to mandatory.
 * @param {boolean} value
 * @param {String} opt_mandatoryMessage An optional mandatory message to display
 * to the user, has no effect unless 'value' is true.
 */
pb.fields.List.prototype.setMandatory = function (value, opt_mandatoryMessage) {
    goog.base(this, 'setMandatory', value, opt_mandatoryMessage);
    var input = this.combo_.getLabelInput().getElement();
    if (value) {
        goog.dom.classes.add(input, "mandatory", "field-mandatory");
    } else {
        goog.dom.classes.remove(input, "mandatory", "field-mandatory");
    }
};

/**
 * Set the field to readonly or not. Also disables the field
 * @param {boolean} value
 */
pb.fields.List.prototype.setReadOnly = function (value) {

    // Do not allow enabling the field in 'View' mode.
    if (this.permanentReadOnly && value == false) return;

    goog.base(this, 'setReadOnly', value);
    this.combo_.setEnabled(!value);
    if (value) {
        goog.dom.classes.add(this.combo_.getLabelInput().getElement(), "readonly");
    } else {
        goog.dom.classes.remove(this.combo_.getLabelInput().getElement(), "readonly");
    }
};

/**
 * Sets if the element should be visible or not. Non visible elements still take
 * up space in the document they just aren't visible.
 * @param {boolean} value
 */
pb.fields.List.prototype.setVisible = function (value) {
    goog.base(this, 'setVisible', value);
    this.element_.style.visibility = (value) ? "visible" : "hidden";
};


/**
 * Abstracts away what type of form input it is and get's the value.
 * For example, for a checkbox a boolean is returned, for a text box a string is 
 * returned. A date field, a date is returned.
 */
pb.fields.List.prototype.getValue = function () {
    return this.combo_.getValue();
};



/**
 * Set the value of the element. 
 * Use <span class="highlight">true</span> and <span class="highlight">false</span> for checkboxes. 
 * @param {Mixed} value
 */
pb.fields.List.prototype.setValue = function (value) {
    
    // --- Prevent infinite loop code ---
    if (this.constructor == pb.fields.List && this.busy) return;
    // Do not want to call this if triggered from a sub class
    if (this.constructor == pb.fields.List) this.busy = true;
    // --- Prevent infinite loop code END ---

    // Do not allow setting a value if in 'View' mode.
    if (this.unchangeable) { this.busy = false; return; }

    if (value !== this.getValue()){
      this.setValueInternal(value);
      this.dispatchEvent("change");
    }   

    // --- Prevent infinite loop code ---
    // Do not want to call this if triggered from a sub class
    if (this.constructor == pb.fields.List) this.busy = false;
    // --- Prevent infinite loop code END ---
}; 
    
/**
 * Set the value without triggering a change event
 */
pb.fields.List.prototype.setValueInternal = function (value) {
     
    // Do not allow setting the value in 'View' mode.
    if (this.unchangeable) return;

    if (value instanceof Promise){
        var self = this;
        value.then(function(value){
            self.setValueInternal(value);
        });
        return;
    }
    
    if (value instanceof pb.fields.AbstractBase) {
        value = value.getValue();
    }

    this.combo_.setValue(value);
    if (value != this.combo_.getValue()) {
        // setValue did not work, so attempt to set value by the caption.
        this.combo_.setValueFromCaption(value);
    }
    
};

/**
 * Make a field completely hidden so that it does not take up space in the 
 * document. The inverse of setDisplay().
 */
pb.fields.List.prototype.setHidden = function (value) {
    goog.base(this, 'setHidden', value);

    if (value) {
        goog.dom.classes.add(this.element_, "hidden");
    } else {
        goog.dom.classes.remove(this.element_, "hidden");
    }
};


/**
 * Get the caption
 */
pb.fields.List.prototype.getCaption = function () {
    var menuItems = this.combo_.getMenu().getItems();
    var value = this.combo_.getValue();
    
    var currentMenuItem = goog.array.find(menuItems, function (item) {
        if (item.getValue() == value)
            return true;
    }, this);

    if (currentMenuItem == null) {
        //    return "";
        return this.combo_.getLabelInput().getValue();
    }

    return currentMenuItem.getCaption();
};


/**
 * Set the caption
 */
pb.fields.List.prototype.setCaption = function (value) {

    // Do not allow changing the value if in 'View' mode.
    if (this.unchangeable) return; 

    this.combo_.getLabelInput().setValue(value);
};

/**
 * Clear options
 */
pb.fields.List.prototype.clearOptions = function () {
          
    // Do not allow changing the value if in 'View' mode.
    if (this.unchangeable) return;

    var previousValue = this.getValue();

    this.clearOptionsSilently();

    // Will force the value to become ''
    if (previousValue != '')
        this.dispatchEvent("change");
};

/**
 * Clear options without triggering a change event
 */
pb.fields.List.prototype.clearOptionsSilently = function () {
    //this.combo_.removeAllItems();
    //this.combo_.setValue("");
    //this.combo_.getLabelInput().setValue("");
    // Don't remove the please select item

    // Do not allow changing the value if in 'View' mode.
    if (this.unchangeable) return;


    this.offDocument();
    this.combo_.clearOptions();
    this.onDocument();
};

/**
 * Add an option to the combo
 */
pb.fields.List.prototype.addOption = function (caption, value, opt_code, opt_indexPos) {
    
    // Do not allow changing the value if in 'View' mode.
    if (this.unchangeable) return;
    
    if (opt_indexPos && opt_indexPos < 0)
        throw "Cannot add option to list at negative position";

    // If the first option is please select then we will abstract this away.
    if (opt_indexPos != null && this.combo_.getItemAt(0) && this.combo_.getItemAt(0).getValue() == "") {
        opt_indexPos = (opt_indexPos + 1);
    }
    if (opt_indexPos == null)
        opt_indexPos = this.combo_.getMenu().getItemCount();
    this.combo_.addItemAt(new pb.ui.ComboBoxItem(caption, value, opt_code), opt_indexPos);
};


/**
 * Set width
 * @param {Number} pixels
 */
pb.fields.List.prototype.setWidth = function (pixels) {
    // 18 for the dropdown arrow width
    this.combo_.getLabelInput().getElement().style.width = (pixels - 21) + "px";
};


/**
 * Get width
 * @return {Number}
 */
pb.fields.List.prototype.getWidth = function () {
    var bounds = goog.style.getBounds(this.combo_.getElement());
    return bounds.width;
};


/**
 * Post-DOM-Building Initialization of the pb.fields.List
 * @private
 */
