/**
 * Static function that creates a field object and decorates the correct
 * container element. This requires the container element to be named correctly,
 * i.e. 'pb-container-list-objectName-propertyName'
 * @param {string} objectName 
 * @param {string} propertyName 
 * @return {pb.fields.List}
 */
pb.fields.List.createAndDecorate = function (objectName, propertyName) {
    var containerId = 'pb-container-' + pb.fields.FieldType.LIST + '-' 
        + objectName + '-' + propertyName;
    var container = goog.dom.getElement(containerId);

    if (!container) {
        throw "Cannot find the container element for the list " +
            "field with object name '" + objectName + "' and property name '"
            + propertyName + "'. Expecting to find an element with id '"
            + containerId + "'.";
    }

    var field = new pb.fields.List(objectName, propertyName);
    field.decorate(container);
    return field;
};


