/**
 * @constructor
 * @param {string} objectName
 * @param {string} propertyName
 * @param {string} dateFormat This is the accepted dateFormat that is passed
 * back to the server.
 * @param {string} opt_otherDateFormats Optionally provide other date formats
 * the user can enter to the field, or set the value as programatically.
 * @param {string} opt_displayDateFormat Optionaly provide an alternate date
 * format that is displayed to the user, use this if you want to display a
 * different date format to the format of the actual stored date value.
 */
pb.fields.DateBase = function (objectName, propertyName, dateFormat,
opt_otherDateFormats, opt_displayDateFormat) {
    goog.base(this, objectName, propertyName);

    this.dateFormat = dateFormat;

    this.otherDateFormats = opt_otherDateFormats || [];

    this.displayDateFormat = opt_displayDateFormat || dateFormat;
};
goog.inherits(pb.fields.DateBase, pb.fields.Text);

/**
 * Builds the pb.fields.Date's DOM tree and stores its root in this.element_.
 */
pb.fields.DateBase.prototype.createDom = function () {
    goog.base(this, 'createDom');                   
    this.displayInput = this.dom_.createDom("input", { "type": "text" });

    // TODO: Only using decorate for now
};


/**
 * Takes an Element that is already present in the document, performs any transformations necessary to turn it into a 
 * structure equivalent to the structure created by createDom(), and stores it in this.element_.
 * @param {Element} element
 */
pb.fields.DateBase.prototype.decorateInternal = function (element) {
    this.displayInput = this.dom_.createDom("input", { "type": "text" });
    
    pb.fields.AbstractBase.prototype.decorateInternal.call(this, element);

    this.input_ = goog.dom.query("input", element)[0];

    /*if (this.input_.name == null) {
        this.input_.name = "ctl00$cntMainBody$" + this.objectName.toUpperCase()
            + "__" + this.propertyName;
    }*/

    // Match any classes
    this.disabledInput_ = this.dom_.createDom("input", {
        "readOnly": "readOnly",
        "class": this.input_.className + " hidden"
    });

    goog.dom.insertSiblingAfter(this.disabledInput_, this.input_);

    if (this.formatter) {
      this.formatter.setBaseValue(this.input_.value);
      this.formatter.decorate(this.input_);
      this.displayInput = this.formatter.getFakeInput();
      this.formatter.setCustomFormatPattern(pattern);
    }


    // Use another field for inputting the date. This field will accept
    // various different date formats.
    var bounds = goog.style.getBounds(this.input_);        
    goog.dom.classes.add(this.displayInput, this.input_.className);
    goog.dom.classes.add(this.input_, "hidden");
    this.displayInput.value = this.input_.value;
    goog.dom.insertSiblingAfter(this.displayInput, this.input_);  
    goog.style.setWidth(this.displayInput, bounds.width);
    goog.style.setHeight(this.displayInput, bounds.height);

    // If the field is disabled then disable it
    if (this.input_.getAttribute("disabled") == "disabled"
        || (this.input_.getAttribute("readOnly") != null
        //  IE returns false if the attribute doesn't exist
        && this.input_.getAttribute("readOnly") != false)) {
        this.setReadOnly(true);
    }


};


/**
 * Set the field to mandatory.
 * @param {boolean} value
 * @param {String} opt_mandatoryMessage An optional mandatory message to display
 * to the user, has no effect unless 'value' is true.
 */
pb.fields.DateBase.prototype.setMandatory = function (value, opt_mandatoryMessage) {
    goog.base(this, 'setMandatory', value, opt_mandatoryMessage);
    if (value) {
        goog.dom.classes.add(this.displayInput, "mandatory", "field-mandatory");
    } else {
        goog.dom.classes.remove(this.displayInput, "mandatory", "field-mandatory");
    }
    if (this.placeholder
        // This check doesn't work
        /*&& this.placeholder.isInDocument()*/) {
        this.placeholder.onBlur();
    }
};

/**
 * Set the field to readonly or not. Also disables the field
 * @param {boolean} value
 */
pb.fields.DateBase.prototype.setReadOnly = function (value) {

  // Do not allow enabling the field in 'View' mode.
  if (this.permanentReadOnly && value == false) return;
    
  // Can't call the text setReadOnly as it makes the hidden input_ visible
  //goog.base(this, 'setReadOnly', value);
  pb.fields.AbstractBase.prototype.setReadOnly.call(this, value);

  pb.fields.AbstractBase.prototype.setReadOnly.call(this, value);
  this.disabledInput_.value = this.input_.value;
  if (value) {
      goog.dom.classes.remove(this.disabledInput_, "hidden");
      goog.dom.classes.add(this.disabledInput_, "readonly");
      goog.dom.classes.add(this.displayInput, "hidden");
  } else {
      goog.dom.classes.add(this.disabledInput_, "hidden");
      goog.dom.classes.remove(this.disabledInput_, "readonly");
      goog.dom.classes.remove(this.displayInput, "hidden");
  }
  goog.dom.classes.add(this.input_, "hidden");
  if (this.placeholder
    // This check doesn't work
    /*&& this.placeholder.isInDocument()*/) {
    this.placeholder.onBlur();

    // Has to be done because the onBlur will give this input a display style
    // of inline and we need it to be hidden if read only
    if (value)
      this.placeholder.getFakeInput().style.display = "none";
  }
};


/**
 * Set the value of the element. 
 * @param {Mixed} value Accepts a date string in one of the accepted formats.
 * Or accepts a date.
 */
pb.fields.DateBase.prototype.setValue = function (date) {

    // --- Prevent infinite loop code ---
    if (this.constructor == pb.fields.DateBase && this.busy) return;
    // Do not want to call this if triggered from a sub class
    if (this.constructor == pb.fields.DateBase) this.busy = true;
    // --- Prevent infinite loop code END ---

    // Do not allow setting a value if in 'View' mode.
    if (this.unchangeable) { this.busy = false; return; }

    if (date instanceof pb.fields.AbstractBase) {
        date = date.valueOf();
    }

    this.setValueInternal(date);
                                   
    // Now convert the date into the string format we are after.
    // If we have a different display format to the value format process it now
    if (this.displayDateFormat != this.dateFormat && this.input_.value != "")
    {
        var displayFormatter = new goog.i18n.DateTimeFormat(this.displayDateFormat);
        var dateStr = displayFormatter.format(date);
        this.displayInput.value = dateStr;
    } else {
        this.displayInput.value = this.input_.value;
    }
    // --- Prevent infinite loop code ---
    // Do not want to call this if triggered from a sub class
    if (this.constructor == pb.fields.DateBase) this.busy = false;
    // --- Prevent infinite loop code END ---

    if (date != this.getValue()){
      this.setValueInternal(date);
      this.dispatchEvent("change");
    }
};

/**
 * Sets the internal value, i.e. the hidden normal input. But does not
 * update the display to the user.
 * @private
 */
pb.fields.DateBase.prototype.setValueInternal = function (date) {

    // Do not allow changing the value in 'View' mode.
    if (this.unchangeable) return;

    if (date instanceof Promise){
        var self = this;
        date.then(function(value){
            self.setValueInternal(value);
        });
        return;
    }

    if ((typeof date) == "string" || date instanceof String) {

        // If an empty string set to no date.
        if (date == "") {
            this.input_.value = date;
            this.disabledInput_.value = date;
            this.dispatchEvent("change");
            return;
        }

        var temp = this.parseDateString(date);
        if (temp != null) {
            date = temp;
        }
    } 


    if (date instanceof Date){
        // Now convert the date into the string format we are after.
        var formatter = new goog.i18n.DateTimeFormat(this.dateFormat);
        var date = formatter.format(date);
    } else {
        //date = "";
    }

    this.input_.value = (date == null) ? "" : date;
    if (this.isReadOnly())
        this.disabledInput_.value = (date == null) ? "" : date;

};


/**
 * Get the value of the field
 * @return {Date}
 */
pb.fields.DateBase.prototype.getValue = function () {
    return this.parseDateString(this.input_.value);
};





/**
 * Post-DOM-Building Initialization of the pb.fields.Text
 * @private
 */
pb.fields.DateBase.prototype.enterDocument = function () {
    goog.base(this, 'enterDocument');

    goog.events.listen(this.displayInput, "keyup", this.validateDisplayInput,
        false, this);

    goog.events.listen(this.displayInput, "blur", function (e) {
        // Remove the temporary classes that give some user indication
        // of the validity of the date they are entering
        goog.dom.classes.remove(this.displayInput, "valid");
        goog.dom.classes.remove(this.displayInput, "partially-valid");
        goog.dom.classes.remove(this.displayInput, "not-valid");

        // Set the display value to the actual value
        this.displayInput.value = this.input_.value;

        // posted back to the server for a date field
		var value = this.parseDateString(this.input_.value);
		if (value == null) this.input_.value = "";
		
		this.dispatchEvent("change");
    }, false, this);
};


/**
 * Validate the display input and indicate to the user if they are entering 
 * a valid date or not. Orange for partially valid, red for invalid and green
 * for a complete date.
 * @private
 */
pb.fields.DateBase.prototype.validateDisplayInput = function () {

    var value = goog.string.trim(this.displayInput.value);

    // Try and get a valid date
    if (value == "") {
        goog.dom.classes.remove(this.displayInput, "valid");
        goog.dom.classes.remove(this.displayInput, "not-valid");

        // Set to no date
        this.setValueInternal(value);
        return;
    }
    var date = this.parseDateString(value);
   
    if (date) {
        goog.dom.classes.add(this.displayInput, "valid");
        goog.dom.classes.remove(this.displayInput, "not-valid");
        // We also update the actual value
        this.setValueInternal(date);
    } else {
        // Even if it's invalid set the value to what was typed in
        this.setValueInternal(value);
        goog.dom.classes.remove(this.displayInput, "valid");
        goog.dom.classes.add(this.displayInput, "not-valid");
    }
};


/**
 * Parse a date string, returns null on parse failure.
 * @param {String} value
 * @returns {Date|null}
 */
pb.fields.DateBase.prototype.parseDateString = function (value) {

    // In order of which to attempt to match against first
    var validDateFormats = goog.array.concat([this.dateFormat],
        this.otherDateFormats);

    return Date.parseDateString(value, validDateFormats);
};


/**
 * Set width
 * @param {Number} pixels
 */
pb.fields.DateBase.prototype.setWidth = function (pixels) {
    goog.base(this, 'setWidth', pixels);
    this.displayInput.style.width = pixels + "px";

    if (this.placeholder
        // This check doesn't work
        /*&& this.placeholder.isInDocument()*/) {
        this.placeholder.onBlur();
    }
};


/**
 * Get width
 * @return {Number}
 */
pb.fields.DateBase.prototype.getWidth = function () {
    var bounds;
    if (this.isReadOnly())
        bounds = goog.style.getBounds(this.disabledInput_);
    else
        bounds = goog.style.getBounds(this.displayInput);

    return bounds.width;
};


/**
 * Set the placeholder value
 */
pb.fields.DateBase.prototype.setPlaceholder = function (value) {
    if (!this.placeholder) {
        this.placeholder = new titan.ui.Placeholder(value);
        this.placeholder.decorate(this.displayInput);
    } else {
        this.placeholder.setValue(value);
    }
};




/**
 * Get the field type
 * @return {pb.fields.FieldType}
 */
pb.fields.DateBase.prototype.getType = function () {
    return pb.fields.FieldType.DATE;
};


/**
 * Set the text alignment on the field.
 * @param {string} alignment Accepts left, centre, center, middle and right.
 */
pb.fields.DateBase.prototype.setTextAlign = function (alignment) {
    goog.base(this, 'setTextAlign', alignment);

    alignment = alignment.toLowerCase();
    if (alignment == "centre" || alignment == "middle") {
        alignment = "center";
    }

    this.displayInput.style.textAlign = alignment;

    if (this.placeholder
        // This check doesn't work
        /*&& this.placeholder.isInDocument()*/) {
        this.placeholder.onBlur();
    }
};

/**
 * Make the input text bold
 * @param {boolean} enable
 */
pb.fields.DateBase.prototype.setBold = function (enable) {
  goog.base(this, 'setBold', enable);
  if (enable) {
    this.displayInput.style.fontWeight = "bold";
  } else {
    this.displayInput.style.fontWeight = "normal";
  }

  if (this.placeholder
    // This check doesn't work
    /*&& this.placeholder.isInDocument()*/) {
    this.placeholder.onBlur();
  }
};

/**
 * Set the formatting pattern.
 * @param {string} pattern
 */
pb.fields.DateBase.prototype.setFormatPattern = function (pattern)
{
  this.displayDateFormat = pattern;
  this.dateFormat = pattern;
};

/**
 * Is the field mandatory
 * @return {Boolean}
 */
pb.fields.DateBase.prototype.isValid = function () {
  // If the display input has a value but the actual input does not then we have
  // in invalid value left in the field
  if (this.displayInput.value != "" && this.input_.value == "") return false;
  if (this.input_.value == "") return true;
  return (this.parseDateString(this.input_.value) != null);
};
