/**
 * @constructor
 * @param {string} objectName
 * @param {string} propertyName
 */
pb.fields.Comment = function (objectName, propertyName) {
    goog.base(this, objectName, propertyName);
};
goog.inherits(pb.fields.Comment, pb.fields.AbstractBase);

/**
 * Builds the pb.fields.Text's DOM tree and stores its root in this.element_.
 */
pb.fields.Comment.prototype.createDom = function () {
    goog.base(this, 'createDom');
    this.element_ = this.dom_.createElement("div");
    this.input_ = this.dom_.createDom("input", {
        "name":    "ctl00$cntMainBody$" + this.objectName.toUpperCase()
            + "__" + this.propertyName,
        "type": "hidden"
    });
    this.disabledInput_ = this.dom_.createDom("textarea", {
        /* Using readonly attribute instead see issue #100 */
        /*disabled: "disabled",*/
        readOnly: "readOnly",
        "class": "hidden"
    });
    this.displayInput_ = this.dom_.createDom("textarea", {
    });
    this.element_.appendChild(this.input_);
    this.element_.appendChild(this.disabledInput_);
    this.element_.appendChild(this.displayInput_);
};


/**
 * Takes an Element that is already present in the document, performs any transformations necessary to turn it into a 
 * structure equivalent to the structure created by createDom(), and stores it in this.element_.
 * @param {Element} element
 */
pb.fields.Comment.prototype.decorateInternal = function (element) {
    goog.base(this, 'decorateInternal', element);

    this.input_ = goog.dom.query("input", element)[0];

    if (this.input_.name == null) {
        this.input_.name = "ctl00$cntMainBody$" + this.objectName.toUpperCase()
            + "__" + this.propertyName;
    }

    // Match any classes
    this.disabledInput_ = this.dom_.createDom("textarea", {
        "readOnly": "readOnly",
        "class": this.input_.className + " hidden"
    });

    this.displayInput_ = this.dom_.createDom("textarea", {
    });

    // Carriage return lines are spat out as &#xD;&#xA; . So replace them.
    //this.input_.value = this.input_.value.replace(/&#xD;&#xA;/g, "\n")
    this.input_.value = goog.string.unescapeEntities(this.input_.value);

    this.displayInput_.value = this.input_.value;

    goog.dom.insertSiblingAfter(this.disabledInput_, this.input_);
    goog.dom.insertSiblingAfter(this.displayInput_, this.input_);


    if (this.input_.getAttribute("disabled") == "disabled"
        || (this.input_.getAttribute("readOnly") != null
        //  IE returns false if the attribute doesn't exist
        && this.input_.getAttribute("readOnly") != false)) {

        this.setReadOnly(true);
    }
};


/**
 * Post-DOM-Building Initialization of the pb.fields.Text
 * @private
 */
pb.fields.Comment.prototype.enterDocument = function () {
    goog.base(this, 'enterDocument');
    goog.events.listen(this.displayInput_, "keyup", function () {
        this.input_.value = this.displayInput_.value;
        this.dispatchEvent("change");
    }, false, this);
    goog.events.listen(this.displayInput_, "blur", function () {
        this.input_.value = this.displayInput_.value;
        this.dispatchEvent("change");
    }, false, this);
};

/**
 * Check if the element can be decorated.
 * Must decorate the parent wrapper div that contains the input
 * @private
 */
pb.fields.Comment.prototype.canDecorate = function (element) {
    
    // Must contain an input
    var inputs = goog.dom.query("input", element);
    if (inputs.length != 1)
        return false;

    return true;
};

/**
 * Set the field to mandatory.
 * @param {boolean} value
 * @param {String} opt_mandatoryMessage An optional mandatory message to display
 * to the user, has no effect unless 'value' is true.
 */
pb.fields.Comment.prototype.setMandatory = function (value, opt_mandatoryMessage) {
    goog.base(this, 'setMandatory', value, opt_mandatoryMessage);
    if (value) {
        goog.dom.classes.add(this.displayInput_, "mandatory", "field-mandatory");
        goog.dom.classes.add(this.disabledInput_, "mandatory", "field-mandatory");
    } else {
        goog.dom.classes.remove(this.displayInput_, "mandatory", "field-mandatory");
        goog.dom.classes.remove(this.disabledInput_, "mandatory", "field-mandatory");
    }
};

/**
 * Set the field to readonly or not. Also disables the field
 * @param {boolean} value
 */
pb.fields.Comment.prototype.setReadOnly = function (value) {

    // Do not allow enabling the field in 'View' mode.
    if (this.permanentReadOnly && value == false) return;

    goog.base(this, 'setReadOnly', value);
    this.disabledInput_.value = this.getDisplayedValue();
    if (value) {
        goog.dom.classes.add(this.displayInput_, "hidden");
        goog.dom.classes.remove(this.disabledInput_, "hidden");
        goog.dom.classes.add(this.disabledInput_, "readonly");
    } else {
        goog.dom.classes.remove(this.displayInput_, "hidden");
        goog.dom.classes.add(this.disabledInput_, "hidden");
        goog.dom.classes.remove(this.disabledInput_, "readonly");
    }
};

