    goog.base(this, 'enterDocument');
    var self = this;
    // Override the select function so that we can trigger a change event
    var temp = window["setctl00_cntMainBody_" + this.objectName + "__" + this.propertyName + "OtherParty"];
    window["setctl00_cntMainBody_" + this.objectName + "__" + this.propertyName + "OtherParty"] = function () {
        temp.apply(this, arguments);
        self.dispatchEvent("change");
    };
