/**
 * @fileoverview Allows an instance that requires multiple settings
 * to have a single object passed in that can be queried to check if it 
 * contains all the needed settings.
 */


goog.provide('titan.structs.ConfigurableSettings');

goog.require('goog.array');

/**
 * @constructor
 */
titan.structs.ConfigurableSettings = function (opt_settings) {
    this.settings = opt_settings || {};
    /**
     * Will have the corresponding setting key set to the type it's converted to 
     * when the setting
     * has been retrieved once, this is to indicate that any type conversion 
     * processing has been done once.
     */
    this.settingsTypeConvertedTo = {};

    /**
     * Stores new values if the settings objects has been converted to a new
     * type.
     */
    this.settingsConverted = {};
};



/**
 * Add a setting
 */
titan.structs.ConfigurableSettings.prototype.add = function (key, value) {
    this.settings[key] = value;
};

/**
 * Alias for add
 */
titan.structs.ConfigurableSettings.prototype.set =
    titan.structs.ConfigurableSettings.prototype.add;

/**
 * Add a setting
 * @param {string} key The setting key.
 * @param {titan.data.GenericJsonObject} opt_type The type to cast the data to, 
 * must implement
 * {titan.data.GenericJsonObject} If the value is currently an array it will
 * cast to this type for each individual value of the array. Changing the type
 * will create a new set of objects and overwrite the previous objects, thus 
 * destroying any object references you may have.
 */
titan.structs.ConfigurableSettings.prototype.get = function (key, 
opt_type) {
    if (opt_type != null) {
        return this.convertSettingToType_(key, opt_type);
    }

    return this.settingsConverted[key] || this.settings[key];
};

/**
 * Converts a settings to a certain type, if the setting value is an array
 * converts to the provided type for each value of the array.
 * @private
 * @param {string} key
 * @param {titan.data.GenericJsonObject} type
 */
titan.structs.ConfigurableSettings.prototype.convertSettingToType_ =
function (key, type) {
    // Don't convert again if we have already done before, this is to preserve
    // object references.
    if (this.settingsTypeConvertedTo[key] == type)
        return this.settingsConverted[key];

    // If there is no value we don't want to convert to a type, as it will
    // give confusing indications that a value exists.
    if (this.settings[key] == undefined)
        return undefined;

    if (this.settings[key] instanceof Array) {
        var newValue = new Array();
        goog.array.forEach(this.settings[key], function (individualValue) {
            newValue.push(new type(individualValue));
        }, this);
        this.settingsConverted[key] = newValue;
    } else {
        this.settingsConverted[key] = new type(this.settings[key]);
    }
    // Set the new values to the settings object, this ensures object
    // references will always match up to the values in the settings
    // object. If we create new objects each time a get is called
    // references will be messed up.
    this.settingsTypeConvertedTo[key] = type;

    return this.settingsConverted[key];
};


