goog.provide('titan.fx.QuickFade');

goog.require('goog.userAgent');

// TODO Maybe swap the callbacks out for events instead.

/**
 * @constructor
 */
titan.fx.QuickFade = function (element, fadeDuration) {
    var self = this;
    this.element_ = element;
    this.fadeDuration = fadeDuration || 2600;

    this.timer = new goog.Timer(20);
    goog.events.listen(this.timer, goog.Timer.TICK, function () {
        self.animate()
    });

    this.mode = titan.fx.QuickFade.Mode.NOTHING;

    this.fadeInCallback = null;
    this.fadeOutCallback = null;
}

/**
 * @enum
 */
titan.fx.QuickFade.Mode = {
    FADING_IN : "fade_in",
    FADING_OUT : "fade_out",
    NOTHING: "nothing"
}

/**
 * Fade in
 * @param {Function} callback Will not be hit if fadeOut is called before this transition has finished.
 */
titan.fx.QuickFade.prototype.fadeIn = function (callback, opt_context) {
    this.fadeInCallback = callback;
    this.fadeInContext = opt_context || this;

    if (this.mode == titan.fx.QuickFade.Mode.FADING_IN){
        return;
    } else if (this.mode == titan.fx.QuickFade.Mode.FADING_OUT){
        var timeElapsed = this.getElapsedTime();
        this.startTime = new Date().getTime() - (this.fadeDuration - timeElapsed);
    } else {
        this.startTime = new Date().getTime();
        this.timer.start();
        this.setOpacity(0);
    }
    this.mode = titan.fx.QuickFade.Mode.FADING_IN;
};

/**
 * Fade out
 */
titan.fx.QuickFade.prototype.fadeOut = function (callback, opt_context) {
    this.fadeOutCallback = callback;
    this.fadeOutContext = opt_context || this;

    if (this.mode == titan.fx.QuickFade.Mode.FADING_OUT) {
        return;
    } else if (this.mode == titan.fx.QuickFade.Mode.FADING_IN) {
        var timeElapsed = this.getElapsedTime();
        this.startTime = new Date().getTime() - (this.fadeDuration - timeElapsed);
    } else {
        this.startTime = new Date().getTime();
        this.timer.start();
        this.setOpacity(1);
    }
    this.mode = titan.fx.QuickFade.Mode.FADING_OUT;
}

/**
 * @private
 */
titan.fx.QuickFade.prototype.setOpacity = function (opacity) {

    if (goog.userAgent.IE && (!goog.userAgent.isVersion(9))){
        //if (goog.userAgent.isVersion(8)){
        // For testing
        //if (opacity > 0.5)
            //throw "";
        this.element_.style.filter = "alpha(opacity=" + (opacity * 100) + ")";
        //} else { 
            //this.element_.style.msFilter = "progid:DXImageTransform.Microsoft.Alpha(Opacity=" + opacity * 100 + ")";
        //}
    } else {
        this.element_.style.opacity = "" + opacity;
    }
    
}

/**
 * Do the math to get what the current opacity should be
 * @param {Number} value This will be a number between 0 and 1. 1 For should be max opacity and 0 for no opacity.
 * Can modify this function to add acceleration, etc.
 * @private
 */
titan.fx.QuickFade.prototype.getOpacity = function (value) {
    return value;
}

/**
 * @private
 */
titan.fx.QuickFade.prototype.animate = function () {
    var timeElapsed = this.getElapsedTime();
    if (timeElapsed > this.fadeDuration) {
        this.timer.stop();
        if (this.mode == titan.fx.QuickFade.Mode.FADING_IN) {
            this.setOpacity(1);
            if (this.fadeInCallback != null)
                this.fadeInCallback.call(this.fadeInContext);
        } else if (this.mode == titan.fx.QuickFade.Mode.FADING_OUT) {
            this.setOpacity(0);
            if (this.fadeOutCallback != null)
                this.fadeOutCallback.call(this.fadeOutContext);
        }
        if (this.mode == titan.fx.QuickFade.Mode.FADING_OUT) {
            var t = 1;
        }
        // Clear the callbacks
        this.fadeInCallback = null;
        this.fadeOutCallback = null;
        this.mode = titan.fx.QuickFade.Mode.NOTHING;
        return;
    }

    var factor = timeElapsed / this.fadeDuration;
    if (this.mode == titan.fx.QuickFade.Mode.FADING_OUT)
        factor = 1 - factor;
    var opacity = this.getOpacity(factor);
    this.setOpacity(opacity);
}

/**
 * @private
 */
titan.fx.QuickFade.prototype.getElapsedTime = function () {
    var currentTime = new Date().getTime();
    return currentTime - this.startTime;
}
