/**
 * Handler class for a table cell
 * @param {Element|Value} [opt_cell] The cell element to use, or a value to use 
 * for it's contents.
 * @constructor
 * @implements {titan.ui.OffDocInterface}
 */
titan.table.Cell = function(opt_cell){
	if (opt_cell == undefined){
		this.cell_ = document.createElement("td");
	} else if (opt_cell.tagName && opt_cell.tagName.toLowerCase() == "td"){
		// Use opt_cell as the cell
		this.cell_ = opt_cell;
	} else {
		// opt_cell is a value or node to append
		this.cell_ = document.createElement("td");
		this.setContents(opt_cell);
	}
	titan.dom.ElementBase.call(this, this.cell_);
};
goog.inherits(titan.table.Cell, titan.dom.ElementBase);

/**
 * The cell element.
 * @private
 */
titan.table.Cell.prototype.cell_ = null;


/**
 * The parent row the cell belongs to
 */
titan.table.Cell.prototype.parentRow = null;

/**
 * Set the contents of the table cell.
 * @param value Accepts a function, element, string or number.
 */
titan.table.Cell.prototype.setContents = function(value){
	titan.table.Cell.setContents(this.cell_, value);
};

titan.table.Cell.setContents = function(cell, value){
    // If we've got a Promise, do it afterwards
    if (value instanceof Promise){
        value.then(function(val){
            titan.table.Cell.setContents(cell, val);  
        });
        return;
    }

	if (goog.isFunction(value)){
		value = value();
	} 
	if (typeof(value) == "string"
		|| typeof(value) == "number"){
		cell.innerHTML = value;
	} else {
		// Assume it is a node
		goog.dom.appendChild(cell, value);
	}
};


/**
 * Take the element out the document, if multiple rows are to be added this will
 * speed up processing if it's not part of the document. Be sure to add it back 
 * to the document afterwards.
 */
titan.table.Cell.prototype.offDocument = function () {
    titan.ui.OffDocInterface.offDocument(this);
};

/**
 * Add the element back into the document.
 */
titan.table.Cell.prototype.onDocument = function () {
    titan.ui.OffDocInterface.onDocument(this);
};

