/**
 * Helper functions for a row element
 * @param {Element|Array} [opt_row] The row element, or an array of 
 * titan.table.Cell or values.
 * @constructor
 * @implements {titan.ui.OffDocInterface}
 */
titan.table.Row = function(opt_row){
	
	// Initilize the cells array
	this.Cells = new Array();
	
	if (opt_row == undefined){
		// Create the row
		opt_row = document.createElement("tr");
		this.row_ = opt_row;
	    titan.dom.ElementBase.call(this, this.row_);
	} else if (opt_row instanceof Array){
	    // Create a row with these cell values
	    this.row_ = document.createElement("tr");
	    titan.dom.ElementBase.call(this, this.row_);
	    for(var i = 0; i < opt_row.length; i++){
	        var cell = opt_row[i];
	        // Add cell
	        this.addCell(cell);
	    }
	} else if (typeof (opt_row) == "string") {
	    throw "string provided, did you forget to wrap the string in an Array?";
	} else {
		// Assume it's a row Element
		// Map the existing table cells
		this.row_ = opt_row;
	    titan.dom.ElementBase.call(this, this.row_);
		this.mapElements();
	}
	
	
};
goog.inherits(titan.table.Row, titan.dom.ElementBase);

/**
 * The row element.
 */
titan.table.Row.prototype.row_ = null;

/**
 * The table the row belongs to
 */
titan.table.Row.prototype.parentTable = null;

/**
 * Add a cell to the row.
 * @param {titan.table.Cell|Element|string|number|Promise} cell The cell element to add
 * @param [index] The position to add the cell, 0 based index. Defaults to the 
 * last position.
 * @returns {titan.table.Cell} The cell added.
 */
titan.table.Row.prototype.addCell = function(cell, index){
	if (cell instanceof titan.table.Cell){
		// Don't do anything as cell is in the format we want.
	} else { 
		cell = new titan.table.Cell(cell);
	}
	
	// Set the index to the last position if it is not provided.
	if (index == undefined){
		index = this.Cells.length;
	}
	
	this.Cells.splice(index, 0, cell);
	var cellElement = cell.getElement();
	
	// This will not work if the row has any other nodes other than
	// td elements.
	// Index with this method is 1 based not 0 based
	goog.dom.insertChildAt(this.row_, cellElement, (index + 1));
	
	cell.parentRow = this;
	return cell;
};


/**
 * Remove a cell
 * @param [index] The position of the cell to remove. Defaults to the last cell.
 * 0 based index.
 */
titan.table.Row.prototype.removeCell = function(index){
	// If index isn't provided, make it equal to the last row.
	if (index == undefined)
		index = this.Cells.length - 1;

	var removedCells = this.Cells.splice(index, 1);
	
	// Index is not 0 based
	var cell = 
        //goog.dom.query("td:nth-child(" + (index + 1) + ")", this.row_)[0]
        goog.dom.findNodes(this.row_, 
            function(el){
                return el.tagName.toUpperCase() == "TD";   
            }    
        )[index]
        || null;
	if(cell == null){
		// Cell doesn't exist
	} else {
		goog.dom.removeNode(cell);
	}
	
	return removedCells[0];
};

/**
 * An array of the row's cells.
 */
 titan.table.Row.prototype.Cells = null;
  
 /**
  * Finds all the cells in the row and maps them to this instance.
  */
 titan.table.Row.prototype.mapElements = function(){
	//var cells = goog.dom.query("td", this.row_);
	var cells = goog.dom.findNode(this.row_, 
        function(el){
            return el.tagName.toUpperCase() == "TD";   
        }    
    );
    for(var i = 0; i < cells.length; i++){
		this.Cells[i] = new titan.table.Cell(cells[i]);
	};
 };


 /**
   * Batch set the cell contents for the row
   * @param {Array<mixed>} contents
   */
 titan.table.Row.prototype.setContents = function (contents) {
     goog.array.forEach(this.Cells, function (cell, index) {
         cell.setContents(contents[index]);
     }, this);
 };


 /**
  * Take the element out the document, if multiple rows are to be added this 
  * will speed up processing if it's not part of the document. Be sure to add it 
  * back to the document afterwards.
  */
 titan.table.Row.prototype.offDocument = function () {
     titan.ui.OffDocInterface.offDocument(this);
 };

 /**
  * Add the element back into the document.
  */
 titan.table.Row.prototype.onDocument = function () {
     titan.ui.OffDocInterface.onDocument(this);
 };





