/**
 * @fileoverview A small api for quering motorweb
 */

goog.provide('pb.motorweb.MotorWeb')
window.pb = window.pb || {};
window.pb.motorweb = window.pb.motorweb || {};
goog.require('titan.structs.ConfigurableSettings');
goog.require('goog.asserts');
goog.require('titan.events');
goog.require('goog.Uri.QueryData');
goog.require('goog.net.XhrIo');
goog.require('pb.ui.AjaxLoader');
goog.require('goog.string');
goog.require('pb.ui.OptionSelectDialog');

/**
 * @constructor
 */
pb.motorweb.MotorWeb = function (settings) {

    this.settings = new titan.structs.ConfigurableSettings(settings);

    this["getAjaxLoader"] = this.getAjaxLoader;
    this["grabAjaxLoader"] = this.grabAjaxLoader;
    this["giveAjaxLoader"] = this.giveAjaxLoader;

    /*
     * This uri must accept the post parameters
     * @param {String} plate
     * @param {String} jurisdiction
     */
    goog.asserts.assertString(this.settings.get("queryPlateUri"),
        "Query plate uri not provided.");
    // Provide this on the server
    //goog.asserts.assertNumber(this.settings.get("accountId"),
    //"Account id not provided.");

    
    this.ajaxLoader = new pb.ui.AjaxLoader();

    this.vehicleSelectDialog = new pb.ui.OptionSelectDialog();

    goog.events.listen(this.vehicleSelectDialog, "dialogselect",
        this.optionSelected, false, this);
};

/**
 * Lookup a plate. If more than one plate is returned, it asks the user to 
 * select one.
 * @param {String} plate
 * @param {String} jurisdiction
 * @param {Function} successCallback Called with the xml of the vehicle as the 
 * first parameter on success.
 * @param {Function} errorCallback Called on error.
 * @param {Object} opt_context Optional context for the callback functions.
 */
pb.motorweb.MotorWeb.prototype.lookupPlate =
function (plate, jurisdiction, successCallback, errorCallback, opt_context) {
    opt_context = opt_context || this;

    
    this.ajaxLoader.setVisible(true);

    var query = new goog.Uri.QueryData();
    query.add("plate", plate);
    query.add("jurisdiction", jurisdiction);
    query.add("seed", goog.string.getRandomString());

    var xhr = new goog.net.XhrIo();
    goog.events.listen(xhr, "success", function () {
        var xml = goog.dom.xml.loadXml(
            xhr.getResponse()
        );
        var errorMessage = this.getXmlError(xml);
        if (errorMessage) {
            var error = new Error(errorMessage);
            errorCallback.call(opt_context, error);
        } else {
            this.handleXml(xml, successCallback, errorCallback, opt_context);
        }

        this.ajaxLoader.setVisible(false);
    }, false, this);
    goog.events.listen(xhr, "error", function () {
        var error = new Error("Network error");
        errorCallback.call(opt_context, error);
        this.ajaxLoader.setVisible(false);
    }, false, this);
    xhr.send(this.settings.get("queryPlateUri") + "?" + query.toString(), "get");
    this.ajaxLoader.setCaption("Retrieving MotorWeb data...");

};

/**
 * Gets a possible error from the xml else returns null
 */
pb.motorweb.MotorWeb.prototype.getXmlError = function (xml) {
    var motorweb = goog.dom.xml.selectNodes(xml, "/MotorWeb")[0] || null;
    if (!motorweb)
        return;

    var errorMessage = motorweb.getAttribute("ErrorMessage");
    return errorMessage;
};


/**
 * Handles the xml returned before calling the success callback, may ask the
 * user to pick a returned vehicle
 * @private
 */
pb.motorweb.MotorWeb.prototype.handleXml = function (xml, successCallback,
errorCallback, context) {
    this.currentCallbackContext = context;
    this.currentSuccessCallback = successCallback;
    this.currentErrorCallback = errorCallback

    var vehicles = goog.dom.xml.selectNodes(xml, "/autoreport/vehicle");
    if (vehicles.length == 0) {
        errorCallback.call(context, new Error("No results returned"));
        return;
    }

    if (vehicles.length == 1) {
        successCallback.call(context, vehicles[0]);
        return;
    }

    // Until the multi select option is tested we will just return
    // the last result for now.
    successCallback.call(context, vehicles[vehicles.length - 1]);
    return;

    this.vehicleSelectDialog.clearOptions();

    goog.array.forEach(vehicles, function (vehicle) {
        // TODO...
        this.vehicleSelectDialog.addOption(this.getDetailsString(vehicle),
            vehicle);
    }, this);
    this.vehicleSelectDialog.setVisible(true);
};


/**
 * Get details string for vehicle.
 */
pb.motorweb.MotorWeb.prototype.getDetailsString = function (vehicle) {

    var vehicleStr = goog.dom.xml.selectNodes(vehicle, "year")[0] || null;
    vehicleStr = (vehicleStr) ? vehicleStr.firstChild.nodeValue : "";
    var make = goog.dom.xml.selectNodes(vehicle, "make")[0] || null;
    make = (make) ? make.firstChild.nodeValue : "";
    var model = goog.dom.xml.selectNodes(vehicle, "model")[0] || null;
    model = (model) ? model.firstChild.nodeValue : "";
    var bodyStyle = goog.dom.xml.selectNodes(vehicle, "body-style")[0] || null;
    bodyStyle = (bodyStyle) ? bodyStyle.firstChild.nodeValue : "";
    var colourPrimary = goog.dom.xml.selectNodes(vehicle, "colour/primary")[0] || null;
    colourPrimary = (colourPrimary) ? colourPrimary.firstChild.nodeValue : "";
    var engineCapacity = goog.dom.xml.selectNodes(vehicle, "engine/capacity")[0] || null;
    engineCapacity = (engineCapacity) ? engineCapacity.firstChild.nodeValue : "";
    var fuelTypePrimary = goog.dom.xml.selectNodes(vehicle, "fuel-type/primary")[0] || null;
    fuelTypePrimary = (fuelTypePrimary) ? fuelTypePrimary.firstChild.nodeValue : "";

    return vehicleStr + ", " + make + ", " + model + "\n" + bodyStyle + ", " + colourPrimary + ", " + engineCapacity + ", " + fuelTypePrimary;

};


/**
 * Option selected
 */
pb.motorweb.MotorWeb.prototype.optionSelected = function (e) {
    
    // There should not be a case where this does not have a value when this is
    // called.
    if (e.value && this.currentSuccessCallback) {
        var vehicle = e.value;
        this.currentSuccessCallback.call(this.currentCallbackContext, vehicle);
    }

    if (e.value == null && this.currentErrorCallback) {
        this.currentErrorCallback.call(
            this.currentCallbackContext,
            new Error()
        );
    }
};



/**
 * Get the ajax loader instance
 */
pb.motorweb.MotorWeb.prototype.getAjaxLoader = function () {
    return this.ajaxLoader;
};


/**
 * Grabs the ajax loader instance, takes ownership of it and is thus from now on
 * unhandled by this motorweb instance.
 */
pb.motorweb.MotorWeb.prototype.grabAjaxLoader = function () {
    if (this.ajaxLoaderGrabbed) {
        // Ownership already given out, but give out the instance again
        return this.ajaxLoaderGrabbedVersion;
    }
    this.ajaxLoaderGrabbedVersion = this.ajaxLoader;
    this.ajaxLoader = new pb.ui.AjaxLoaderInterface();
    return this.ajaxLoaderGrabbedVersion;
};

/**
 * Gives the instance back ownership of the ajax loader
 * @param {pb.ui.AjaxLoaderInterface} Use a different object for the ajax loader
 */
pb.motorweb.MotorWeb.prototype.giveAjaxLoader = function (opt_ajaxLoader) {
    if (!this.ajaxLoaderGrabbed) {
        // Allready has ownership
        return;
    }
    this.ajaxLoader = opt_ajaxLoader || this.ajaxLoaderGrabbedVersion;
    this.ajaxLoaderGrabbedVersion = null;
};

/**
 * @fileoverview It's common to have a rule that uses a value, the value can
 * be updated if it changes, an optional condition and else value can also be
 * used.
 */


goog.provide('pb.ValueWhenHelper');

/**
 * ValueWhenHelper
 * @constructor
 * @param {pb.Expression} value
 * @param {pb.Expression} opt_condition
 * @param {pb.Expression} opt_elseValue
 */
pb.ValueWhenHelper = function (value, opt_condition, opt_elseValue) {
    goog.base(this);

    this["valueOf"] = this.valueOf;

    this.value = value;
    this.condition = opt_condition || true;
    this.elseValue = opt_elseValue;
    
    goog.events.listen(value, "change", this.updateValue, false, this);
    if (opt_condition) {
        goog.events.listen(opt_condition, "change", this.updateCondition,
            false, this);
    }
    if (opt_elseValue) {
        goog.events.listen(opt_elseValue, "change", this.updateElse, false,
            this);
    }
};
goog.inherits(pb.ValueWhenHelper, goog.events.EventTarget);



/**
 * Only dispatches a change if the condition is true (same as when not provided)
 * @private
 */
pb.ValueWhenHelper.prototype.updateValue = function () {
    if (this.condition.valueOf()) {
        this.dispatchEvent({
            type: "change",
            target: this
        })
    }
};

/**
 * Dispatches a change event
 */
pb.ValueWhenHelper.prototype.updateCondition = function () {
    // If an else value if not provided then the value will be undefined,
    // this should be checked before setting a value
    this.dispatchEvent({
        type: "change",
        target: this
    })
};

/**
 * Only dispatches a change if the condition is false
 */
pb.ValueWhenHelper.prototype.updateElse = function () {
    if (!this.condition.valueOf()) {
        this.dispatchEvent({
            type: "change",
            target: this
        })
    }
};


/**
 * Value of
 */
pb.ValueWhenHelper.prototype.valueOf = function () {
    if (this.condition == true) {
        return this.value.valueOf();
    } else if (this.elseValue) {
        return this.elseValue.valueOf();
    }

    // Has no value, returns undefined
};


/**
 * @fileoverview Handles the layout of columns, as there is nothing to 
 * generate a css handled column layout, we will do the job with JavaScript for
 * now.
 * If you had the number of columns 2 this will break down the html structure
 * from
 *  <ul>
 *      <li>1</li>
 *      <li>2</li>
 *      <li>3</li>
 *      <li>4</li>
 *      <li>5</li>
 *      <li>6</li>
 *  </ul>
 * to
 * <table>
 *  <tr>
 *      <td><ul><li></li></ul></td>
 *      <td><ul><li></li></ul></td>
 *  </tr>
 *  <tr>
 *      <td><ul><li></li></ul></td>
 *      <td><ul><li></li></ul></td>
 *  </tr>
 *  <tr>
 *      <td><ul><li></li></ul></td>
 *      <td><ul><li></li></ul></td>
 *  </tr>
 * It preserves the ul structure to prevent breaking other code that relies on
 * it.
 */

goog.provide('pb.dom.ColumnOrganiser');
window.pb = window.pb || {};
window.pb.dom = window.pb.dom || {};
goog.require('goog.ui.Component');
goog.require('pb.dom.ColumnLayout');

/**
 * @param {goog.dom.DomHelper=} opt_domHelper Optional DOM helper.
 * @constructor
 * @extends {goog.ui.Component}
 */
pb.dom.ColumnOrganiser = function (opt_domHelper) {
    goog.base(this, opt_domHelper);
};
goog.inherits(pb.dom.ColumnOrganiser, goog.ui.Component);

/**
 * Builds the itl.ui.CategoryDataDisplay's DOM tree and stores its root in 
 * this.element_.
 */
pb.dom.ColumnOrganiser.prototype.createDom = function () {
    this.decorateInternal(this.dom_.createElement('div'));
};


/**
 * Takes an Element that is already present in the document, performs any 
 * transformations necessary to turn it into a structure equivalent to the 
 * structure created by createDom(), and stores it in this.element_.
 * @param {Element} element
 */
pb.dom.ColumnOrganiser.prototype.decorateInternal = function (element) {
    this.setElementInternal(element);

    this.columnsNo_ = element.getAttribute("data-column-count") || 1;
    this.columnsNo_ = window["parseInt"](this.columnsNo_);
    this.columnRatios_ = element.getAttribute("data-column-ratio");
    this.layout = (element.getAttribute("data-layout"))
        ? element.getAttribute("data-layout").toLowerCase() : null;

    if (this.columnRatios_) {
        this.columnRatios_ = this.columnRatios_.split(":");
        this.columnRatios_ = goog.array.map(this.columnRatios_, function (ratio) {
            return window["parseFloat"](ratio);
        }, this);
    } else {
        this.columnRatios_ = [];
        for (var i = 0; i < this.columnsNo_; i++) {
            this.columnRatios_[i] = 100 / this.columnsNo_;
        }
    }

    var lis = goog.array.filter(this.ulElement_.childNodes, function(childEle){
        if (childEle.tagName && childEle.tagName.toUpperCase() == "LI"
            && (!goog.dom.classes.has(childEle, "hidden-dre")))
            return true;
    }, this);
    var dreFields = goog.array.filter(this.ulElement_.childNodes,
        function (dreEle) {
            if (goog.dom.classes.has(dreEle, "hidden-dre")) {
                return true;
            }
        }, this);

    var table = document.createElement("div");
    table.className = "column-organiser";
    // Create ul elements for each row
    var tr;
    goog.array.forEach(lis, function (li, i) {
        if ((i % this.columnsNo_) == 0) {
            if (tr) {
                if (this.layout == pb.dom.ColumnLayout.NOFLOW)
                    table.appendChild(tr);
            }
            tr = this.dom_.createDom("div");
        }
        goog.dom.classes.add(tr, "co-row");
        // Set the ratio
        var td = document.createElement("div");
        goog.dom.classes.add(td, "co-cell");

        td.style.cssFloat = "left";
        td.style.styleFloat = "left";
        
        var innerul = document.createElement("ul");
        td.appendChild(innerul);
        innerul.appendChild(li);

        /*while (li.childNodes.length > 0){
            td.appendChild(li.childNodes[0]);
        };*/

        td.style.width = this.columnRatios_[i % this.columnsNo_] + "%";
        
        if (this.layout == pb.dom.ColumnLayout.NOFLOW)
            tr.appendChild(td);
        else 
            table.appendChild(td);
        
        if ((i % this.columnsNo_) == this.columnsNo_ - 1) {
            if (this.layout == pb.dom.ColumnLayout.NOFLOW) {
                goog.dom.classes.add(td, "last");

                var temp;
                tr.appendChild(temp = document.createElement("div"));
                temp.style.clear = "both";
            }
            
        }
        // If last li
        if (lis.length == i + 1) {
            var remainder = ((i + 1 + this.columnsNo_) % this.columnsNo_);
            remainder = (remainder != 0) ? this.columnsNo_ - remainder : 0;
            while (remainder > 0) {
                var tempTd = document.createElement("div");
                goog.dom.classes.add(tempTd, "co-cell");

                tempTd.style.cssFloat = "left";
                tempTd.style.styleFloat = "left";
                
                if (this.layout == pb.dom.ColumnLayout.NOFLOW)
                    tr.appendChild(tempTd);
                else
                    table.appendChild(tempTd);
                remainder--;
            }
            if (this.layout == pb.dom.ColumnLayout.NOFLOW)
                table.appendChild(tr);
        }
    }, this);
    if (tr)
        goog.dom.classes.add(tr, "last");
    element.appendChild(table);
    
    goog.dom.removeNode(this.ulElement_);

    // Add the hidden dre fields in
    goog.array.forEach(dreFields, function (dreField) {
        // Ensure it's hidden
        dreField.style.display = "none";
        element.appendChild(dreField);
    }, this);

    
};


/**
 * Post-DOM-Building Initialization of the pb.dom.ColumnOrganiser
 * @private
 */
pb.dom.ColumnOrganiser.prototype.enterDocument = function () {

};

/**
 * Check it's in correct stucture
 */
