/**
 * @param {goog.dom.DomHelper=} opt_domHelper Optional DOM helper.
 * @constructor
 * @extends {goog.ui.Component}
 * @param  {string} xpath The xpath location of the objects you want to display
 * in the table. The page window.XmlDataSet will be queried. Or can directly 
 * pass in the xml nodes to render.
 * @param {Array<string>} columns
 */
pb.ui.XmlDataSetTable = function (xpath, columns, opt_domHelper) {
    goog.base(this, opt_domHelper);

    this.columns_ = columns;

    // Add rows for the xml objects.
    this.xmlDocument_ = pb.xml.Document.loadXml(window["XMLDataSet"]);
    if (typeof(xpath) == "string"){
        this.tableObjects_ = this.xmlDocument_.selectNodes(xpath);
    } else {
        this.tableObjects_ = xpath;
    }
    this.numberFormatter_ = new goog.i18n.NumberFormat(
        goog.i18n.currency.getLocalCurrencyPattern(window["PolicyCurrencyCode"] || "GBP"));
};
goog.inherits(pb.ui.XmlDataSetTable, goog.ui.Component);

/**
 * Builds the itl.ui.CategoryDataDisplay's DOM tree and stores its root in 
 * this.element_.
 */
pb.ui.XmlDataSetTable.prototype.createDom = function () {
    this.decorateInternal(this.dom_.createElement('table'));
};


/**
 * Takes an Element that is already present in the document, performs any 
 * transformations necessary to turn it into a structure equivalent to the 
 * structure created by createDom(), and stores it in this.element_.
 * @param {Element} element
 */
pb.ui.XmlDataSetTable.prototype.decorateInternal = function (element) {
    this.setElementInternal(element);

    // Find the thead
    var thead = goog.dom.query("thead", element)[0];
    var columnFormatting = this.workoutColumnFormats(thead);

    // Find the tbody
    var tbody = goog.dom.query("tbody", element)[0];
    // Get existing nodes
    var existingElements = tbody.childNodes;

    // If any special rows have been provided put them into variables.
    goog.array.forEach(existingElements, function (element) {
        if (element.tagName && element.tagName.toUpperCase() == "tr"
            && goog.dom.classes.has(element, "total")) {
            this.totalRow_ = element;
            // Map the td nodes
            this.totalRowTds_ = goog.array.filter(
                this.totalRow_.childNodes,
                function (element) {
                    return (element.tagName.toUpperCase() == "TD")
                }, this);
        }
    }, this);

    
    goog.array.forEach(this.tableObjects_, function (obj) {
        var row = new pb.ui.XmlDataSetRow(obj, this.columns_, columnFormatting);
        row.render(tbody);

        // If we've got a total row then add up totals if required
        if (this.totalRow_) {
            // Create a new array if not done already
            this.totals_ = this.totals_ || [];

            goog.array.forEach(this.columns_, function(col, i){
                var td = this.totalRowTds_[i];
                // If this column should be summed
                if (goog.dom.classes.has(td, "sum")
                    || goog.dom.classes.has(td, "avg")) {
                    this.totals_[i] += window["parseFloat"](
                        obj.getAttribute(col));
                }
            }, this);
        }
    }, this);

    // Populate the total row cells if it exists
    if (this.totalRow_) {
        goog.array.forEach(this.totalRowTds_, function (td, i) {
            var value;
            if (goog.dom.classes.has(td, "sum")) {
                value = this.totals_[i] || 0;
            } else if (goog.dom.classes.has(td, "avg")) {
                value = (this.totals_[i] && this.tableObjects_.length > 0) ?
                    this.totals_[i] / this.tableObjects_.length
                    : 0;
            }
            // If the currency class is used, format the value
            if (this.columnFormatting[i] & pb.ui.XmlDataSetTable.ColumnFormat.CURRENCY) {
                value = this.numberFormatter_.format(value);
            }

            td.innerHTML = value;
        }, this);
    }
};


/**
 * Post-DOM-Building Initialization of the pb.ui.XmlDataSetTable
 * @private
 */
pb.ui.XmlDataSetTable.prototype.enterDocument = function () {

};

/**
 * Works out how columns should be formatted, this is done by reading classes
 * attached to table header cells in the thead. Current supported classes
 * are 'currency'.
 * @returns {Array<Number>} An array of bitmasks representing the different
 * formatting types.
 */
pb.ui.XmlDataSetTable.prototype.workoutColumnFormats = function (thead) {
    var columnStates = [];

    goog.array.forEach(goog.dom.query("tr", thead), function (tr) {

        var colIndex = 0;
        goog.array.forEach(goog.dom.query("th", tr), function (th) {


            var colSpan = th.getAttribute("colspan") || 1;
            colSpan = ((typeof colSpan) == "string")
                ? window["parseInt"](colSpan)
                : colSpan;

            while (colSpan > 0) {
                columnStates[colIndex] = columnStates[colIndex] || 0;
                // Add the curreny format state to the state bit mask
                if (goog.dom.classes.has(th, "currency")) {
                    columnStates[colIndex] = columnStates[colIndex] |
                        pb.ui.XmlDataSetTable.ColumnFormat.CURRENCY;
                }
                colIndex++;
                colSpan--;
            }

            

        }, this);


    }, this);

    return columnStates;
};

/**
 * The column format bit mask states
 */
pb.ui.XmlDataSetTable.ColumnFormat = {
    CURRENCY: 1
};

