/**
 * Get the address
 */
pb.ui.MSI_PropertySummaryTable.prototype.getTitle = function () {
    var addressLine1 = this.locationNode_.getAttribute("AddressLine1Txt"),
        addressLine2 = this.locationNode_.getAttribute("AddressLine2Txt"),
        addressLine3 = this.locationNode_.getAttribute("AddressLine3Txt"),
        addressLine4 = this.locationNode_.getAttribute("AddressLine4Txt"),
        townCity = this.locationNode_.getAttribute("TownCityTxt"),
        county = this.locationNode_.getAttribute("CountyTxt"),
        postCode = this.locationNode_.getAttribute("PostcodeTxt");

    var parts = [];
    if (addressLine1) parts.push(addressLine1);
    if (addressLine2) parts.push(addressLine2);
    if (addressLine3) parts.push(addressLine3);
    if (addressLine4) parts.push(addressLine4);
    if (townCity) parts.push(townCity);
    if (county) parts.push(county);
    if (postCode) parts.push(postCode);

    return parts.join(",");
};


/**
 * Add the table contents
 * @protected
 */
pb.ui.MSI_PropertySummaryTable.prototype.createContent = function () {
    this.tbody = new titan.table.Tbody(this.tbodyElement);

    this.declaredTotal = 0, this.finalDiscountPremiumTotal = 0,
    this.expiredFinalDiscountPremiumTotal = 0;


    var materialDamages = this.locationNode_.selectNodes("./MD_InsuredItem");
    goog.array.forEach(materialDamages, function (node) {
        this.addIndependentNodeItem.call(this, node);
    }, this);





    // Only do MD Items if this flag is not 0
    var mdFlag = Field.getInstance("RISKCOVERDETAILS", "MaterialDamage_MD_Flag").getValue(),
        biFlag = Field.getInstance("RISKCOVERDETAILS", "MaterialDamage_BI_Flag").getValue();

    // Add Floating Items
    if (mdFlag !== 0 && mdFlag !== "0") {
        this.addFloatingItem.call(this, "MD_FloatingBuildings", "Floating buildings");
        this.addFloatingItem.call(this, "MD_FloatingStock", "Floating stock");
        this.addFloatingItem.call(this, "MD_FloatingContents", "Floating contents");
        this.addFloatingItem.call(this, "MD_FloatingComputers", "Floating computers");
        this.addFloatingItem.call(this, "MD_FloatingTenantsImprovements", "Floating Tenents Improvements");
        this.addFloatingItem.call(this, "MD_FloatingRentPayable", "Floating Rent payable");
        this.addFloatingItem.call(this, "MD_FloatingOther", "Floating other - " + this.locationNode_.getAttribute("MD_FloatingOtherTxt"));
    }
    // Add all the location material damages
    var biItems = this.locationNode_.selectNodes("./BI_InsuredItem");
    goog.array.forEach(biItems, function (node) {
        this.addIndependentNodeItem.call(this, node);
    }, this);
        
    if (biFlag !== 0 && biFlag !== "0") {
        this.addFloatingItem.call(this, "BI_FloatingGrossProfit", "Floating Gross profit");
        this.addFloatingItem.call(this, "BI_FloatingGrossRevenue", "Floating Gross revenue");
        this.addFloatingItem.call(this, "BI_Floating_ICoW_", "Floating ICoW");
        this.addFloatingItem.call(this, "BI_Floating_AICoW_", "Floating Additional ICoW");
        this.addFloatingItem.call(this, "BI_FloatingRentReceivable", "Floating Rent Receivable");
        this.addFloatingItem.call(this, "BI_FloatingOther", "Floating other - " + this.locationNode_.getAttribute("BI_FloatingOtherTxt"));
    }
    this.addTotalRow();
};

