/**
 * Get the premium amount total.
 * @return {number}
 * @protected
 */
pb.ui.MSI_PropertySummaryTable.prototype.addTotalRow = function () {

    // Add a total row
    var totalRow = this.tbody.addRow([
        this.getTotalRowDescription(),
        //this.formatter.format(this.declaredTotal),
        this.formatterInt.format(this.declaredTotal),
        "",
        this.formatter.format(this.finalDiscountPremiumTotal)
    ]);
    // Set colour of row
    goog.array.forEach(totalRow.Cells, function (cell) {
        cell.getElement().style.backgroundColor = "#ffff80";
    });

    this.premiumTotal = this.finalDiscountPremiumTotal;
};



/**
 * Add a row
 * @private
 */
pb.ui.MSI_PropertySummaryTable.prototype.addRow =
function (description, rate, premium, expiringPremium, declaredAmount) {


    this.declaredTotal += declaredAmount;
    this.finalDiscountPremiumTotal += premium;
    this.expiredFinalDiscountPremiumTotal += expiringPremium;


    // Don't add if sum insured is 0
    if (declaredAmount == 0 && premium == 0 && expiringPremium == 0) return;


    //declaredAmount = this.formatter.format(declaredAmount);
    // declaredAmount should show only whole numbers
    declaredAmount = this.formatterInt.format(declaredAmount);
    premium = this.formatter.format(premium);

    var percentFormatter;
    //if (this.customFormatPattern != null) {
    //    percentFormatter = new goog.i18n.NumberFormat(this.customFormatPattern);
    //} else {
        if (Math.round(rate) != rate && Math.round(rate * 10000) != Math.round(rate) * 10000) {
            percentFormatter = new goog.i18n.NumberFormat(pb.formatting.Percentage.formatDecimals);
        } else {
            percentFormatter = new goog.i18n.NumberFormat(pb.formatting.Percentage.format);
        }
    //}


    rate = percentFormatter.format(rate / 100);


    this.tbody.addRow([
        description,
        declaredAmount,
        rate,
        premium
    ]);
};


