/**
 * Builds the itl.ui.CategoryDataDisplay's DOM tree and stores its root in 
 * this.element_.
 */
pb.ui.MSI_ExpiringPremiumTable.prototype.createDom = function () {

    var isBadIEVersion = (goog.userAgent.IE && (!goog.userAgent.isVersion(10)));
    
    var table = this.dom_.createElement('table');

    var container;
    if (isBadIEVersion) {
        container = this.dom_.createDom("div", null, table);
        container.style.position = "relative";
        //container.style.display = "inline-block";
        //goog.dom.classes.add(container, "goog-inline-block");
    } else {
        container = table;
    }

    var headerCell, subHeaderRow, headSection;
    table.appendChild(headSection = this.dom_.createDom("thead", null));


    if (isBadIEVersion) {
        var topHeaderLine = this.dom_.createDom("table", 
            { "style": "margin:0;" },
            this.dom_.createDom("thead", null,
                this.dom_.createDom("tr", null,
                    headerCell = this.dom_.createDom("th", { "colSpan": "5" })
                )
            )
        );
        topHeaderLine.style.width = "100%";
        //headerCell.style.textAlign = "left";
        goog.dom.insertSiblingBefore(topHeaderLine, table);

        table.style.marginTop = "0";
    } else {
        headSection.appendChild(this.dom_.createDom("tr", null,
            headerCell = this.dom_.createDom("th", { "colSpan": "5" })
        ));
    }
    headSection.appendChild(subHeaderRow = this.dom_.createDom("tr"));




    table.appendChild(this.tbodyElement = this.dom_.createDom("tbody"));

    var floatingLocation = !!window["parseInt"](this.locationNode_.getAttribute("FL"));
    
    headerCell.innerHTML = this.getTitle();
    
    /*var htmlString = "<th style=\"width: 25%;\">Insured Item Type</th>"
        + "<th style=\"width: 25%;\" class=\"currency\">Sum Insured</th>"
        + "<th style=\"width: 25%;\" class=\"currency\">Final Discounted Gross Rate</th>"
        + "<th style=\"width: 25%;\" class=\"currency\">Final Discounted Gross Premium</th>";
    if (goog.userAgent.IE && !goog.userAgent.isVersion(9)) {
        subHeaderRow.appendChild(
            goog.dom.htmlToDocumentFragment(htmlString)
        );
    } else {
        subHeaderRow.innerHTML = htmlString;
    }*/
    var cell1, cell2, cell3, cell4, cell5;
    subHeaderRow.appendChild(cell1 = this.dom_.createDom("th", { "style": "width: 20%;" }));
    subHeaderRow.appendChild(cell2 = this.dom_.createDom("th", { "style": "width: 20%;", "class": "currency" }));
    subHeaderRow.appendChild(cell3 = this.dom_.createDom("th", { "style": "width: 20%;", "class": "currency" }));
    subHeaderRow.appendChild(cell4 = this.dom_.createDom("th", { "style": "width: 20%;", "class": "currency" }));
    subHeaderRow.appendChild(cell5 = this.dom_.createDom("th", { "style": "width: 20%;", "class": "percentage" }));

    var columnHeaders = this.getColumnHeaders();

    cell1.innerHTML = columnHeaders[0];
    cell2.innerHTML = columnHeaders[1];
    cell3.innerHTML = columnHeaders[2];
    cell4.innerHTML = columnHeaders[3];
    cell5.innerHTML = columnHeaders[4];
    

    this.setElementInternal(container);

    // Create currency format pattern
    var pattern = pb.formatting.Currency.format;
    this.formatterInt = new goog.i18n.NumberFormat(pattern.slice(0, pattern.lastIndexOf(".")));
    this.formatter = new goog.i18n.NumberFormat(pattern);

    this.createContent();
};


/**
 * Get column headers
 * @protected
 */
pb.ui.MSI_ExpiringPremiumTable.prototype.getColumnHeaders = function () {
    return [
        "Insured Item Type",
        "Declared Amount",
        "Renewal Final Discounted Net Premium",
        "Expiring Final Discounted Net Premium",
        "% Difference"
    ];
};


/**
 * Add a row
 * @protected
 */
pb.ui.MSI_ExpiringPremiumTable.prototype.addRow =
function (description, rate, premium, expiringPremium, sumInsured, includeZeroSI) {


    this.declaredTotal += sumInsured;
    this.finalDiscountPremiumTotal += premium;
    this.expiredFinalDiscountPremiumTotal += expiringPremium;

    // Don't add if sumInsured is 0
    if (!includeZeroSI && (sumInsured == 0 && premium == 0 && expiringPremium == 0)) return;

    

    var difference = pb.ui.MSI_ExpiringPremiumTable.getPremiumDifference(premium, expiringPremium);
    
    //sumInsured = this.formatter.format(sumInsured);
    // Sum insured should only show whole numbers
    sumInsured = this.formatterInt.format(sumInsured);
    premium = this.formatter.format(premium);
    expiringPremium = this.formatter.format(expiringPremium);


    this.tbody.addRow([
        description,
        sumInsured,
        premium,
        expiringPremium,
        difference
    ]);
};



/**
 * Get the premium amount total.
 * @return {number}
 * @protected
 */
pb.ui.MSI_ExpiringPremiumTable.prototype.addTotalRow = function () {

    var difference = pb.ui.MSI_ExpiringPremiumTable.getPremiumDifference(this.finalDiscountPremiumTotal, this.expiredFinalDiscountPremiumTotal);

    // Add a total row
    var totalRow = this.tbody.addRow([
        this.getTotalRowDescription(),
        this.formatterInt.format(this.declaredTotal),
        //this.formatter.format(this.declaredTotal),
        this.formatter.format(this.finalDiscountPremiumTotal),
        this.formatter.format(this.expiredFinalDiscountPremiumTotal),
        difference
    ]);
    // Set colour of row
    goog.array.forEach(totalRow.Cells, function (cell) {
        cell.getElement().style.backgroundColor = "#ffff80";
    });

    this.premiumTotal = this.finalDiscountPremiumTotal;
};

/**
 * Gets a string of the percentage difference between the expiring premium
 * and the new premium.
 * @return {string}
 * @private
 */
pb.ui.MSI_ExpiringPremiumTable.getPremiumDifference = 
function (premium, expiringPremium) {
    var difference = premium - expiringPremium;
    difference = difference / expiringPremium;

    if (difference == -0)
        difference = 0;

    var percentFormatter;
    if (expiringPremium != 0 && expiringPremium != null && expiringPremium != "") {
        //if (this.customFormatPattern != null) {
        //    percentFormatter = new goog.i18n.NumberFormat(this.customFormatPattern);
        //} else {
            if (Math.round(difference) != difference && Math.round(difference * 10000) != Math.round(difference) * 10000) {
                percentFormatter = new goog.i18n.NumberFormat(pb.formatting.Percentage.formatDecimals);
            } else {
                percentFormatter = new goog.i18n.NumberFormat(pb.formatting.Percentage.format);
            }
        //}

        difference = percentFormatter.format(difference);
    } else {
        difference = "n/a";
    }

    if (difference == "-0%")
        difference = "0%";

    return difference;
}

