/**
 * @fileoverview This object is responsible for delaying any DOM manipulation 
 * before all the html is rendered. Any DOM manipulation is then done in one go 
 * requiring only one re-paint of the effected area, thus speeding up DOM load 
 * time.
 */

goog.provide('pb.dom.QuickerDom');
goog.require('goog.ui.Component');
goog.require('goog.dom');
goog.require('goog.dom.query');

/**
 * @param {goog.dom.DomHelper=} opt_domHelper Optional DOM helper.
 * @constructor
 * @extends {goog.ui.Component}
 */
pb.dom.QuickerDom = function (opt_domHelper) {
    this.postponedFunctions = [];
};

/**
 * Takes the element out of the document and performs any dom manipulation
 * that has been postponed on it.
 * @param {Element} element
 */
pb.dom.QuickerDom.prototype.decorate = function (element) {
    var previousNode = goog.dom.getPreviousNode(element);
    var parentNode = goog.dom.getParentElement(element);
    goog.dom.removeNode(element);
    // Do dom manipulation, although document.getElementById will not work.
    var document = {
        getDocumentByElementId: function (id) {
            return goog.dom.query("#" + id, element)[0];
        }
    };

    var errorsOnPage = [];
    goog.array.forEach(this.postponedFunctions, function (func) {
        try {
            func.call(element);
        } catch (e) {
            // Supressing the error for now so the rest of the page can load
            errorsOnPage.push(e);
            if (console) {
                console.log(e);
            }
        }
    }, this);
    // Add the element back into the document
    if (previousNode) {
        goog.dom.insertSiblingAfter(element, previousNode);
    } else {
        goog.dom.insertChildAt(parentNode, element, 0);
    }
    
    // Throw the errors now that the page has finished loading
    goog.array.forEach(errorsOnPage, function (err) {
        throw err;

        // Script will stop after one error so looping through them is most
        // likely futile.
    }, this);

};


/**
 * Postpone some dom manipulation code
 * @param {Function} func
 */
pb.dom.QuickerDom.prototype.postpone = function (func) {
    this.postponedFunctions.push(func);
};
/**
 * @fileoverview The functions provided in TabIndex.js in pure are buggy. So
 * these functions are intended to override them. The overwriting of the 
 * existing functions is done in exports.js
 */

goog.provide('pb.TabIndex');

/**
 * Add a tab id to the list of visible tabs
 */
pb.TabIndex = function ()
{
    /**
     * An array of the visible tab ids.
     * @type Array<string>
     */
    this.visibleTabs = [];

    /**
     * An array of the hidden tab ids.
     * @type Array<string>
     */
    this.hiddenTabs = [];
}
goog.addSingletonGetter(pb.TabIndex);

/**
 * Marks the tab to be shown
 * @param {string} tabId
 */
pb.TabIndex.prototype.showTab = function (tabId){
    // Add it to the visible tabs
    if (!goog.array.contains(this.visibleTabs, tabId))
    {
        this.visibleTabs.push(tabId);
    } 
    // Remove it from the hidden tabs
    var index = goog.array.indexOf(this.hiddenTabs, tabId);
    if (index != -1)
    {
        goog.array.removeAt(this.hiddenTabs, index);
    } 

    // Show the physical tab
    var tabElement = 
        document.getElementById('ctl00_cntMainBody_TabIndex' + '_' + tabId);
    tabElement.style.display = '';  

    this.updateInputs_();
};


/**
 * Marks the tab to be hidden
 * @param {string} tabId
 */
pb.TabIndex.prototype.hideTab = function (tabId){
    // Add it to the hidden tabs
    if (!goog.array.contains(this.hiddenTabs, tabId))
    {
        this.hiddenTabs.push(tabId);
    } 
    // Remove it from the visible tabs
    var index = goog.array.indexOf(this.visibleTabs, tabId);
    if (index != -1)
    {
        goog.array.removeAt(this.visibleTabs, index);
    } 

    // Hide the physical tab
    var tabElement = 
        document.getElementById('ctl00_cntMainBody_TabIndex' + '_' + tabId);
    tabElement.style.display = 'none';  

    this.updateInputs_();
};

/**
 * Update the input fields on the page that pass back which tabs are hidden
 * and which are not.
 * @private
 */
pb.TabIndex.prototype.updateInputs_ = function (){
    // Sort the arrays, not the most efficient way, as could be added in the
    // sorted position originally. Keep sorted to make easier on backend code.
    goog.array.sort(this.hiddenTabs, pb.TabIndex.alphanumSort);
    goog.array.sort(this.visibleTabs, pb.TabIndex.alphanumSort);

    var hiddenTabsInput = 
        document.getElementById('ctl00_cntMainBody_TabIndex_inpHiddenTabs'),
        visibleTabsInput = 
        document.getElementById('ctl00_cntMainBody_TabIndex_inpVisibleTabs');

    hiddenTabsInput.value = this.hiddenTabs.join(",");
    visibleTabsInput.value = this.visibleTabs.join(",");
};

/**
 * Alphanumeric sort taken from.
 * http://web.archive.org/web/20130826203933/http://my.opera.com/GreyWyvern/blog/show.dml/1671288
 */
pb.TabIndex.alphanumSort = function (a, b)
{
    function chunkify(t)
    {
        var tz = [], x = 0, y = -1, n = 0, i, j;
        
        while (i = (j = t.charAt(x++)).charCodeAt(0))
        {
            var m = (i == 46 || (i >= 48 && i <= 57));
            if (m !== n)
            {
                tz[++y] = "";
                n = m;
            }
            tz[y] += j;
        }
        return tz;
    }
    
    var aa = chunkify(a);
    var bb = chunkify(b);
    
    for (x = 0; aa[x] && bb[x]; x++)
    {
        if (aa[x] !== bb[x])
        {
            var c = Number(aa[x]), d = Number(bb[x]);
            if (c == aa[x] && d == bb[x])
            {
                return c - d;
            } else return (aa[x] > bb[x]) ? 1 : -1;
        }
    }
    return aa.length - bb.length;
};
